import React, { useEffect, useState } from 'react';

const WorkersList = () => {
  const [workers, setWorkers] = useState([]);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [formData, setFormData] = useState({ location: '', doorNumber: '' });
  const [distance, setDistance] = useState('');
  const [arrivalTime, setArrivalTime] = useState('');
  const [bookedWorkerIds, setBookedWorkerIds] = useState([]);
  const [workerCoords, setWorkerCoords] = useState(null);
  const [userCoords, setUserCoords] = useState(null);
  const [showMap, setShowMap] = useState(false);
  const [loadingCoords, setLoadingCoords] = useState(false);
  const [coordsError, setCoordsError] = useState(null);
  const [showBookingPopup, setShowBookingPopup] = useState(false);

  useEffect(() => {
    const fetchWorkers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/workers');
        const data = await res.json();
        setWorkers(data);
      } catch (err) {
        console.error('Failed to fetch workers:', err);
      }
    };

    const fetchUserLocation = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/users/me'); // adjust if needed
        const data = await res.json();
        if (data.latitude && data.longitude) {
          setUserCoords({ lat: data.latitude, lng: data.longitude });
        }
      } catch (err) {
        console.error('Failed to fetch user location:', err);
      }
    };

    fetchWorkers();
    fetchUserLocation();
  }, []);

  const fetchCoordsFromAddress = async (address) => {
    try {
      setLoadingCoords(true);
      setCoordsError(null);
      const url = `https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(address)}&format=json&limit=1`;
      const response = await fetch(url);
      const data = await response.json();
      if (data.length > 0) {
        return { lat: parseFloat(data[0].lat), lng: parseFloat(data[0].lon) };
      } else {
        setCoordsError('Location not found');
        return null;
      }
    } catch (err) {
      setCoordsError('Error fetching location');
      return null;
    } finally {
      setLoadingCoords(false);
    }
  };

  const handleTrack = async (worker) => {
    setWorkerCoords(null);
    setCoordsError(null);
    setShowMap(false);
    const addressToSearch = worker.location || worker.address || worker.name;
    const coords = await fetchCoordsFromAddress(addressToSearch);
    if (coords) {
      setWorkerCoords(coords);
      setShowMap(true);
      alert(`Tracking worker location: Lat ${coords.lat}, Lng ${coords.lng}`);
      // You can replace alert with map showing logic later
    }
  };

  const handleBookClick = (worker) => {
    setSelectedWorker(worker);
    setFormData({ location: '', doorNumber: '' });
    setDistance('');
    setArrivalTime('');
    setShowBookingPopup(true);
  };

  const handleBookingFormChange = (e) => {
    const { name, value } = e.target;
    const updatedFormData = { ...formData, [name]: value };
    setFormData(updatedFormData);

    // Show distance and arrival time if both fields are filled
    if (updatedFormData.location && updatedFormData.doorNumber) {
      const randomDistance = (Math.random() * (10 - 2) + 2).toFixed(2);
      const randomTime = Math.floor(Math.random() * (25 - 10) + 10);
      setDistance(`${randomDistance} km`);
      setArrivalTime(`${randomTime} minutes`);
    } else {
      setDistance('');
      setArrivalTime('');
    }
  };

  const handleConfirmBooking = async () => {
    if (!formData.location || !formData.doorNumber) {
      alert('Please enter location and door number.');
      return;
    }
    try {
      const res = await fetch(`http://localhost:5000/api/workers/${selectedWorker._id}/book`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      if (res.ok) {
        alert('Booking confirmed and email sent!');
        setWorkers((prev) =>
          prev.map((w) => (w._id === selectedWorker._id ? { ...w, available: false } : w))
        );
        setBookedWorkerIds((prev) => [...prev, selectedWorker._id]);
        setShowBookingPopup(false);
        setSelectedWorker(null);
        setFormData({ location: '', doorNumber: '' });
        setDistance('');
        setArrivalTime('');
        // Reset availability after 5 minutes
        setTimeout(() => {
          setWorkers((prev) =>
            prev.map((w) => (w._id === selectedWorker._id ? { ...w, available: true } : w))
          );
          setBookedWorkerIds((prev) => prev.filter(id => id !== selectedWorker._id));
        }, 5 * 60 * 1000);
      } else {
        const errorData = await res.json();
        console.error('Booking error:', errorData);
        alert('Failed to book the worker.');
      }
    } catch (err) {
      console.error('Booking failed:', err);
      alert('Booking failed due to network/server error.');
    }
  };

  const handleContact = () => {
    if (selectedWorker && selectedWorker.phone) {
      window.location.href = `tel:${selectedWorker.phone}`;
    } else {
      alert('Phone number not available.');
    }
  };

  const handleMessage = () => {
    alert('Message feature not implemented yet.');
  };

  const handleClosePopup = () => {
    setShowBookingPopup(false);
    setSelectedWorker(null);
    setFormData({ location: '', doorNumber: '' });
    setDistance('');
    setArrivalTime('');
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '2rem' }}>Workers List</h2>
      {workers.length === 0 ? (
        <p style={{ textAlign: 'center' }}>No workers found.</p>
      ) : (
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '1.5rem' }}>
          {workers.map((worker) => (
            <div
              key={worker._id}
              style={{
                border: '1px solid #ddd',
                borderRadius: '12px',
                padding: '1rem',
                boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
                textAlign: 'center',
              }}
            >
              {worker.photo ? (
                <img
                  src={`http://localhost:5000/uploads/${worker.photo}`}
                  alt={worker.name}
                  style={{
                    width: '100px',
                    height: '100px',
                    objectFit: 'cover',
                    borderRadius: '50%',
                    marginBottom: '1rem',
                  }}
                />
              ) : (
                <div
                  style={{
                    width: '100px',
                    height: '100px',
                    borderRadius: '50%',
                    backgroundColor: '#eee',
                    margin: '0 auto 1rem',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '14px',
                    color: '#888',
                  }}
                >
                  No Photo
                </div>
              )}
              <h3>{worker.name}</h3>
              <p><strong>Work:</strong> {worker.workSpecification || 'N/A'}</p>
              <p><strong>Experience:</strong> {worker.experience || 'N/A'}</p>
              <p><strong>Location:</strong> {worker.location || 'N/A'}</p>
              <p><strong>Phone:</strong> {worker.phone || 'N/A'}</p>
              <p>
                <strong>Status:</strong>{' '}
                <span style={{ color: worker.available ? 'green' : 'red' }}>
                  {worker.available ? 'Available' : 'Unavailable'}
                </span>
              </p>
              <button
                disabled={!worker.available}
                onClick={() => handleBookClick(worker)}
                style={{
                  padding: '0.5rem 1rem',
                  marginTop: '0.5rem',
                  backgroundColor: worker.available ? '#007bff' : '#999',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: worker.available ? 'pointer' : 'not-allowed',
                }}
              >
                Book Now
              </button>
            </div>
          ))}
        </div>
      )}

      {/* Booking Popup */}
      {showBookingPopup && selectedWorker && (
        <div
          style={{
            position: 'fixed',
            top: 0, left: 0,
            width: '100vw',
            height: '100vh',
            backgroundColor: 'rgba(0,0,0,0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 1000,
          }}
        >
          <div
            style={{
              backgroundColor: '#fff',
              borderRadius: '12px',
              padding: '2rem',
              maxWidth: '400px',
              width: '90%',
              boxShadow: '0 5px 15px rgba(0,0,0,0.3)',
              position: 'relative',
            }}
          >
            {/* Close button */}
            <button
              onClick={handleClosePopup}
              style={{
                position: 'absolute',
                top: '10px',
                right: '10px',
                background: 'transparent',
                border: 'none',
                fontSize: '1.5rem',
                cursor: 'pointer',
              }}
              aria-label="Close"
            >
              &times;
            </button>

            {/* Worker Photo */}
            {selectedWorker.photo ? (
              <img
                src={`http://localhost:5000/uploads/${selectedWorker.photo}`}
                alt={selectedWorker.name}
                style={{
                  width: '120px',
                  height: '120px',
                  borderRadius: '50%',
                  objectFit: 'cover',
                  display: 'block',
                  margin: '0 auto 1rem',
                }}
              />
            ) : (
              <div
                style={{
                  width: '120px',
                  height: '120px',
                  borderRadius: '50%',
                  backgroundColor: '#eee',
                  margin: '0 auto 1rem',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '16px',
                  color: '#888',
                }}
              >
                No Photo
              </div>
            )}

            <h2 style={{ textAlign: 'center', marginBottom: '1rem' }}>{selectedWorker.name}</h2>
            <p><strong>Experience:</strong> {selectedWorker.experience || 'N/A'}</p>
            <p><strong>Location:</strong> {selectedWorker.location || 'N/A'}</p>
            <p><strong>Phone:</strong> {selectedWorker.phone || 'N/A'}</p>

            {/* Booking form */}
            <div style={{ marginTop: '1rem' }}>
              <input
                type="text"
                name="location"
                placeholder="Your Location"
                value={formData.location}
                onChange={handleBookingFormChange}
                style={{ width: '100%', padding: '0.5rem', marginBottom: '0.5rem' }}
              />
              <input
                type="text"
                name="doorNumber"
                placeholder="Door Number"
                value={formData.doorNumber}
                onChange={handleBookingFormChange}
                style={{ width: '100%', padding: '0.5rem', marginBottom: '0.5rem' }}
              />
              {distance && arrivalTime && (
                <p><strong>Distance:</strong> {distance}, <strong>Estimated Arrival Time:</strong> {arrivalTime}</p>
              )}
            </div>

            {/* Buttons: Book, Track, Contact, Message */}
            <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '1.5rem' }}>
              <button
                onClick={handleConfirmBooking}
                style={{
                  backgroundColor: '#28a745',
                  color: '#fff',
                  border: 'none',
                  padding: '0.5rem 1rem',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  flex: 1,
                  marginRight: '0.5rem',
                }}
              >
                Book
              </button>
              <button
                onClick={() => handleTrack(selectedWorker)}
                style={{
                  backgroundColor: '#007bff',
                  color: '#fff',
                  border: 'none',
                  padding: '0.5rem 1rem',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  flex: 1,
                  marginRight: '0.5rem',
                }}
              >
                Track
              </button>
              <button
                onClick={handleContact}
                style={{
                  backgroundColor: '#ffc107',
                  color: '#000',
                  border: 'none',
                  padding: '0.5rem 1rem',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  flex: 1,
                  marginRight: '0.5rem',
                }}
              >
                Contact
              </button>
              <button
                onClick={handleMessage}
                style={{
                  backgroundColor: '#17a2b8',
                  color: '#fff',
                  border: 'none',
                  padding: '0.5rem 1rem',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  flex: 1,
                }}
              >
                Message
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default WorkersList;
