import React, { useContext, useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const [showDropdown, setShowDropdown] = useState(false);
  const [popupImageUrl, setPopupImageUrl] = useState('');
  const [showPopup, setShowPopup] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchWorkerData = async () => {
      if (user?.role === 'worker') {
        try {
          const res = await fetch(`http://localhost:5000/api/workers/${user._id}`);
          const data = await res.json();

          const qrCode = data.qrcode || data.qrCode;
          const fallbackPhoto = data.photo;

          const imageToShow = qrCode ? qrCode : fallbackPhoto;
          setPopupImageUrl(`http://localhost:5000/uploads/${imageToShow}`);
        } catch (err) {
          console.error("Failed to fetch worker QR code or photo:", err);
        }
      }
    };

    fetchWorkerData();
  }, [user]);

  const handleLogout = async () => {
    await logout();
    navigate('/');
  };

  return (
    <>
      <style>{`
        .nav-link {
          color: white;
          text-decoration: none;
          font-weight: 500;
          margin: 0 1rem;
        }
        .nav-link:visited,
        .nav-link:hover,
        .nav-link:active,
        .nav-link:focus {
          color: white;
          text-decoration: none;
        }
      `}</style>

      <nav style={styles.navbar}>
        <div style={styles.logo}>ðŸŒ¿FixItNow</div>
        <div style={{ display: 'flex', alignItems: 'center', position: 'relative' }}>
          <Link className="nav-link" to="/">Home</Link>
          <Link className="nav-link" to="/workerslist">Services</Link>

          {user?.role === 'worker' && (
            <>
              <Link className="nav-link" to="/inbox">Inbox</Link>
              <button
                style={styles.qrButton}
                onClick={() => setShowPopup(true)}
              >
                Show QR
              </button>
            </>
          )}

          {!user ? (
            <>
              <Link className="nav-link" to="/signup">Signup</Link>
              <Link className="nav-link" to="/login">Login</Link>
            </>
          ) : (
            <div
              style={styles.accountIcon}
              onClick={() => setShowDropdown(!showDropdown)}
            >
              <img
                src={`http://localhost:5000/uploads/${user.photo}`}
                alt="Profile"
                style={styles.profileImage}
              />
              {showDropdown && (
                <div style={styles.dropdown}>
                  <div style={styles.dropdownItem}>{user.name}</div>
                  <div style={styles.dropdownItem} onClick={handleLogout}>Logout</div>
                </div>
              )}
            </div>
          )}
        </div>
      </nav>

      {showPopup && (
        <div style={styles.popupOverlay}>
          <div style={styles.popup}>
            <button style={styles.closeBtn} onClick={() => setShowPopup(false)}>Ã—</button>
            <h3>{popupImageUrl.includes('qrcode') ? 'Your QR Code' : 'Your Photo'}</h3>
            {popupImageUrl ? (
              <img
                src={popupImageUrl}
                alt="Popup Image"
                style={{
                  width: '200px',
                  height: '200px',
                  marginTop: '10px',
                  border: '1px solid #ccc',
                  borderRadius: '8px',
                }}
              />
            ) : (
              <p>Image not found.</p>
            )}
          </div>
        </div>
      )}
    </>
  );
};

const styles = {
  navbar: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: '1rem 2rem',
    backgroundColor: '#111827',
    color: 'white',
    alignItems: 'center',
  },
  logo: {
    fontWeight: 'bold',
    fontSize: '1.5rem',
  },
  accountIcon: {
    display: 'inline-block',
    marginLeft: '1rem',
    cursor: 'pointer',
    position: 'relative',
  },
  profileImage: {
    width: '35px',
    height: '35px',
    borderRadius: '50%',
    objectFit: 'cover',
    cursor: 'pointer',
    border: '2px solid white',
  },
  dropdown: {
    position: 'absolute',
    top: '2.5rem',
    right: 0,
    backgroundColor: '#fff',
    color: '#000',
    border: '1px solid #ccc',
    borderRadius: '5px',
    overflow: 'hidden',
    zIndex: 999,
  },
  dropdownItem: {
    padding: '0.5rem 1rem',
    cursor: 'pointer',
    borderBottom: '1px solid #eee',
  },
  qrButton: {
    marginLeft: '1rem',
    backgroundColor: '#10b981',
    color: 'white',
    border: 'none',
    padding: '6px 12px',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  popupOverlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100vw',
    height: '100vh',
    backgroundColor: 'rgba(0,0,0,0.6)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 9999,
  },
  popup: {
    backgroundColor: '#fff',
    padding: '20px',
    borderRadius: '10px',
    textAlign: 'center',
    position: 'relative',
    width: '300px',
  },
  closeBtn: {
    position: 'absolute',
    top: '8px',
    right: '10px',
    border: 'none',
    background: 'none',
    fontSize: '20px',
    cursor: 'pointer',
  },
};

export default Navbar;
