import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const Inbox = () => {
  const [messages, setMessages] = useState([]);
  const { user } = useContext(AuthContext);

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const res = await axios.get(`/api/messages/worker/${user.phone}`);
        if (Array.isArray(res.data)) {
          setMessages(res.data);
        } else {
          setMessages([]); // fallback if response is not an array
        }
      } catch (error) {
        console.error('Failed to fetch messages:', error);
        setMessages([]); // fallback on error
      }
    };

    if (user?.role === 'worker') {
      fetchMessages();
    }
  }, [user]);

  return (
    <div style={{ padding: '20px' }}>
      <h2>ðŸ“¥ Inbox</h2>
      {messages.length === 0 ? (
        <p>No messages yet.</p>
      ) : (
        <ul>
          {messages.map((msg, index) => (
            <li key={index} style={{ marginBottom: '15px' }}>
              <strong>From:</strong> {msg.senderPhone}<br />
              <strong>Message:</strong> {msg.text}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Inbox;
