import React, { useState } from 'react';
import axios from 'axios';

const Message = ({ senderId, receiverId, onClose }) => {
  const [content, setContent] = useState('');

  const sendMessage = async () => {
    try {
      await axios.post('http://localhost:5000/api/messages', {
        senderId,
        receiverId,
        content,
      });
      alert('Message sent!');
      onClose(); // Close popup or modal after sending
    } catch (error) {
      console.error('Error sending message:', error);
      alert('Failed to send message');
    }
  };

  return (
    <div style={{ padding: '20px', background: '#fff', border: '1px solid #ccc' }}>
      <h3>Send Message</h3>
      <textarea
        value={content}
        onChange={(e) => setContent(e.target.value)}
        placeholder="Type your message..."
        rows={4}
        style={{ width: '100%' }}
      />
      <br />
      <button onClick={sendMessage} style={{ marginTop: '10px' }}>Send</button>
    </div>
  );
};

export default Message;
