import React, { useEffect, useState } from 'react';

const WorkersList = () => {
  const [workers, setWorkers] = useState([]);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [formData, setFormData] = useState({ location: '', doorNumber: '' });
  const [showInfo, setShowInfo] = useState(false);
  const [distance, setDistance] = useState('');
  const [arrivalTime, setArrivalTime] = useState('');
  const [bookedWorkerIds, setBookedWorkerIds] = useState([]);

  useEffect(() => {
    const fetchWorkers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/workers');
        const data = await res.json();
        setWorkers(data);
      } catch (err) {
        console.error('Failed to fetch workers:', err);
      }
    };

    fetchWorkers();
  }, []);

  const handleBookNow = (worker) => {
    setSelectedWorker(worker);
    setFormData({ location: '', doorNumber: '' });
    setShowInfo(false);
    setDistance('');
    setArrivalTime('');
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    const updatedFormData = { ...formData, [name]: value };
    setFormData(updatedFormData);
    if (updatedFormData.location && updatedFormData.doorNumber) {
      const randomDistance = (Math.random() * (10 - 2) + 2).toFixed(2);
      const randomTime = Math.floor(Math.random() * (25 - 10) + 10);
      setDistance(`${randomDistance} km`);
      setArrivalTime(`${randomTime} minutes`);
      setShowInfo(true);
    }
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`http://localhost:5000/api/workers/${selectedWorker._id}/book`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      if (res.ok) {
        setWorkers((prev) =>
          prev.map((w) => (w._id === selectedWorker._id ? { ...w, available: false } : w))
        );
        setBookedWorkerIds((prev) => [...prev, selectedWorker._id]);
        setSelectedWorker(null);
        setFormData({ location: '', doorNumber: '' });
        setShowInfo(false);
        setDistance('');
        setArrivalTime('');
        setTimeout(() => {
          setWorkers((prev) =>
            prev.map((w) => (w._id === selectedWorker._id ? { ...w, available: true } : w))
          );
        }, 5 * 60 * 1000);
      } else {
        const errorData = await res.json();
        console.error('Booking error:', errorData);
        alert('Failed to book the worker.');
      }
    } catch (err) {
      console.error('Booking failed:', err);
      alert('An unexpected error occurred while booking.');
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h2 style={{ textAlign: 'center' }}>Workers List</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
        {workers.map((worker) => (
          <div
            key={worker._id}
            style={{
              border: '1px solid #ddd',
              borderRadius: '10px',
              padding: '20px',
              margin: '10px',
              width: '250px',
              textAlign: 'center',
              boxShadow: '0 0 10px rgba(0,0,0,0.1)',
            }}
          >
            <img
              src={worker.image || 'https://via.placeholder.com/150'}
              alt={worker.name}
              style={{
                width: '100px',
                height: '100px',
                borderRadius: '50%',
                objectFit: 'cover',
                marginBottom: '10px',
              }}
            />
            <h3>{worker.name}</h3>
            <p><strong>Work:</strong> {worker.work}</p>
            <p><strong>Experience:</strong> {worker.experience}</p>
            <p><strong>Location:</strong> {worker.location || 'N/A'}</p>
            <p><strong>Phone:</strong> {worker.phone || 'N/A'}</p>
            <p>
              <strong>Status:</strong>{' '}
              <span style={{ color: worker.available ? 'green' : 'red' }}>
                {worker.available ? 'Available' : 'Unavailable'}
              </span>
            </p>
            <button
              onClick={() => handleBookNow(worker)}
              style={{
                backgroundColor: '#4285F4',
                color: '#fff',
                border: 'none',
                padding: '10px 20px',
                borderRadius: '5px',
                cursor: 'pointer',
                marginTop: '10px',
              }}
              disabled={!worker.available}
            >
              Book Now
            </button>
          </div>
        ))}
      </div>

      {selectedWorker && (
        <div style={{ marginTop: '20px', textAlign: 'center' }}>
          <h3>Booking for {selectedWorker.name}</h3>
          <form onSubmit={handleFormSubmit} style={{ display: 'inline-block', textAlign: 'left' }}>
            <div style={{ marginBottom: '10px' }}>
              <label>Location:</label>
              <input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleInputChange}
                style={{ marginLeft: '10px', padding: '5px', borderRadius: '5px', border: '1px solid #ddd' }}
              />
            </div>
            <div style={{ marginBottom: '10px' }}>
              <label>Door Number:</label>
              <input
                type="text"
                name="doorNumber"
                value={formData.doorNumber}
                onChange={handleInputChange}
                style={{ marginLeft: '10px', padding: '5px', borderRadius: '5px', border: '1px solid #ddd' }}
              />
            </div>
            {showInfo && (
              <div style={{ marginBottom: '10px' }}>
                <p><strong>Distance:</strong> {distance}</p>
                <p><strong>Arrival Time:</strong> {arrivalTime}</p>
              </div>
            )}
            <button
              type="submit"
              style={{
                backgroundColor: '#4285F4',
                color: '#fff',
                border: 'none',
                padding: '10px 20px',
                borderRadius: '5px',
                cursor: 'pointer',
              }}
            >
              Confirm Booking
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default WorkersList;
