import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

const HeroSection = () => {
  const [workers, setWorkers] = useState([]);

  useEffect(() => {
    const fetchWorkers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/workers');
        const data = await res.json();
        setWorkers(data);
      } catch (err) {
        console.error('Failed to fetch workers:', err);
      }
    };
    fetchWorkers();
  }, []);

  return (
    <div
      style={{
        width: '100%',
        marginLeft: '0',
        background: 'linear-gradient(to bottom, #0d001c, #000)',
        minHeight: '70vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '2rem 0',
        color: 'white',
        fontFamily: 'Arial, sans-serif',
        overflow: 'hidden',
        position: 'relative'
      }}
    >
      <div
        style={{
          width: '100%',
          maxWidth: '1300px',
          display: 'flex',
          flexDirection: 'row',
          justifyContent: 'space-between',
          alignItems: 'center',
          gap: '2rem',
          flexWrap: 'wrap',
        }}
      >
        {/* LEFT TEXT SECTION */}
        <div style={{ maxWidth: '900px', zIndex: 10 }}>
          <h1
            style={{
              fontSize: '3rem',
              fontWeight: 800,
              lineHeight: '1.2',
              overflow: 'hidden',
              display: 'inline-block',
              animation: 'slideFromTop 5s ease-in-out infinite',
            }}
          >
            <span style={{ color: '#FDE047', fontSize: '3.5rem' }}>✦</span>{' '}
            WORKER SPOTLIGHT
          </h1>

          <p
            style={{
              marginTop: '1rem',
              color: '#aaa',
              fontSize: '1.1rem',
              overflow: 'hidden',
              whiteSpace: 'nowrap',
              display: 'inline-block',
              animation: 'moveText 10s linear infinite',
            }}
          >
            Your trusted platform to find and book skilled workers instantly.
          </p>

          <style>
            {`
              @keyframes slideFromTop {
                0% {
                  transform: translateY(-100%);
                  opacity: 0;
                }
                50% {
                  transform: translateY(0);
                  opacity: 1;
                }
                100% {
                  transform: translateY(-100%);
                  opacity: 0;
                }
              }

              @keyframes moveText {
                0% { transform: translateX(100%); }
                100% { transform: translateX(-100%); }
              }

              @keyframes moveUp {
                0% { transform: translateY(100%); opacity: 0; }
                10% { opacity: 1; }
                90% { opacity: 1; }
                100% { transform: translateY(-120%); opacity: 0; }
              }
            `}
          </style>
        </div>

        {/* RIGHT MOVING WORKER CARDS */}
        <div style={{
          position: 'relative',
          width: '300px',
          height: '400px',
          overflow: 'hidden',
        }}>
          {workers.slice(0, 3).map((worker, index) => (
            <div
              key={worker._id}
              style={{
                position: 'absolute',
                width: '100%',
                top: '100%',
                animation: `moveUp 9s ease-in-out ${index * 3}s infinite`,
                background: 'rgba(255, 255, 255, 0.05)',
                border: '1px solid rgba(255, 255, 255, 0.2)',
                borderRadius: '1rem',
                padding: '1rem',
                backdropFilter: 'blur(15px)',
                WebkitBackdropFilter: 'blur(15px)',
                color: 'white',
                textAlign: 'center',
                boxShadow: '0 8px 32px rgba(0,0,0,0.25)',
              }}
            >
              {worker.photo ? (
                <img
                  src={`http://localhost:5000/uploads/${worker.photo}`}
                  alt={worker.name}
                  style={{
                    width: '80px',
                    height: '80px',
                    borderRadius: '50%',
                    objectFit: 'cover',
                    marginBottom: '1rem',
                    border: '2px solid #fff'
                  }}
                />
              ) : (
                <div
                  style={{
                    width: '80px',
                    height: '80px',
                    borderRadius: '50%',
                    backgroundColor: '#eee',
                    margin: '0 auto 1rem',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    color: '#555'
                  }}
                >
                  No Photo
                </div>
              )}
              <h3 style={{ margin: '0.5rem 0' }}>{worker.name}</h3>
              <p><strong>Skill:</strong> {worker.workSpecification || 'N/A'}</p>
              <p><strong>Location:</strong> {worker.location || 'N/A'}</p>
              <p style={{ color: worker.available ? '#2e7d32' : '#c62828' }}>
                {worker.available ? 'Available' : 'Booked'}
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HeroSection;
