import React from 'react';
import Services from '../components/Services';
import WorkersList from '../components/WorkersList';
import bluecolar from './bluecolar.mp4';

const Home = () => {
  return (
    <>
      {/* Hero Section with Background Video */}
      <div style={styles.heroSection}>
        <video autoPlay loop muted style={styles.backgroundVideo}>
          <source src={bluecolar} type="video/mp4" />
          Your browser does not support the video tag.
        </video>

        <div style={styles.welcomeContent}>
          <h1>Empowering Blue-Collar Workers Everywhere</h1>
          <p>Connecting skilled workers with meaningful job opportunitiesâ€”fast, simple, and secure.</p>
        </div>
      </div>

      {/* Services Section */}
      <Services />

      {/* Workers List Section (optional) */}
      {/* <WorkersList /> */}
    </>
  );
};

const styles = {
  heroSection: {
    position: 'relative',
    minHeight: '100vh',
    overflow: 'hidden',
    color: 'white',
  },
  backgroundVideo: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    objectFit: 'cover',
    zIndex: -1,
    filter: 'brightness(0.5)',
  },
  welcomeContent: {
    position: 'relative',
    height: '100vh',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    textAlign: 'center',
    padding: '0 2rem',
    textShadow: '2px 2px 4px #000',
  },
};

export default Home;
