import React, { useState, useEffect } from 'react';

const HeroSection = () => {
  const [workers, setWorkers] = useState([]);
  const [usersCount, setUsersCount] = useState(0);
  const [workersCount, setWorkersCount] = useState(0);

  useEffect(() => {
    const fetchCounts = async () => {
      try {
        const workerRes = await fetch('http://localhost:5000/api/workers');
        const workerData = await workerRes.json();
        setWorkers(workerData);
        setWorkersCount(workerData.length);

        const userRes = await fetch('http://localhost:5000/api/users');
        const userData = await userRes.json();
        setUsersCount(userData.length);
      } catch (err) {
        console.error('Failed to fetch counts:', err);
      }
    };
    fetchCounts();
  }, []);

  return (
    <div
      style={{
        width: '100vw',
        margin: 0,
        background: '#1c1c1c',
        minHeight: '90vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '2rem',
        color: 'white',
        fontFamily: 'Arial, sans-serif',
        textAlign: 'center',
      }}
    >
      <h1 style={{ fontSize: '3rem', fontWeight: 'bold', marginBottom: '1rem' }}>
        Welcome to Our Worker Platform
      </h1>
      <p style={{ fontSize: '1.2rem', maxWidth: '800px', marginBottom: '2rem', color: '#ccc' }}>
        Discover and connect with skilled professionals near you to get your work done efficiently.
        We aim to bridge the gap between customers and workers seamlessly.
      </p>

      <div style={{ display: 'flex', gap: '3rem', flexWrap: 'wrap', justifyContent: 'center' }}>
        <div style={{ background: '#333', padding: '1.5rem 2rem', borderRadius: '10px' }}>
          <h2 style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>{usersCount}</h2>
          <p>Total Users</p>
        </div>

        <div style={{ background: '#333', padding: '1.5rem 2rem', borderRadius: '10px' }}>
          <h2 style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>{workersCount}</h2>
          <p>Total Workers</p>
        </div>
      </div>

      <p style={{ marginTop: '2rem', fontSize: '1rem', color: '#aaa' }}>
        Empowering local workers. Building stronger communities.
      </p>
    </div>
  );
};

export default HeroSection;
