import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const HeroSection = () => {
  const [workers, setWorkers] = useState([]);
  const [currentWorker, setCurrentWorker] = useState(null);

  useEffect(() => {
    const fetchWorkers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/workers');
        const data = await res.json();
        setWorkers(data);
        if (data.length > 0) {
          setCurrentWorker(data[Math.floor(Math.random() * data.length)]);
        }
      } catch (err) {
        console.error('Failed to fetch workers:', err);
      }
    };
    fetchWorkers();
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      if (workers.length > 0) {
        setCurrentWorker(workers[Math.floor(Math.random() * workers.length)]);
      }
    }, 20000);

    return () => clearInterval(interval);
  }, [workers]);

  return (
    <div
      style={{
        width: '100vw',
        margin: '0',
        padding: '0',
        background: 'linear-gradient(to bottom, #0d001c, #000)',
        minHeight: '85vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: 'white',
        fontFamily: 'Arial, sans-serif',
        overflow: 'hidden',
      }}
    >
      <div
        style={{
          width: '100%',
          maxWidth: '1300px',
          display: 'flex',
          flexDirection: 'row',
          justifyContent: 'space-between',
          alignItems: 'center',
          gap: '0rem',
          flexWrap: 'wrap',
        }}
      >
        {/* LEFT TEXT SECTION with animation */}
        <motion.div
          initial={{ y: -50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 1, ease: 'easeOut' }}
          style={{ maxWidth: '600px', zIndex: 10 }}
        >
          <h1 style={{ fontSize: '3rem', fontWeight: 800, lineHeight: '1.2', margin: 0 }}>
            <span style={{ color: '#FDE047', fontSize: '3.5rem' }}>âœ¦</span>{' '}
            WORKER SPOTLIGHT
          </h1>
          <p style={{ marginTop: '1rem', color: '#aaa', fontSize: '1.1rem' }}>
            Meet our skilled workers, explore and book instantly.
          </p>
        </motion.div>

        {/* RIGHT ANIMATION & WORKER CARD */}
        <div style={{ position: 'relative', width: '350px', height: '350px' }}>
          {/* ROTATING BUBBLE */}
          <motion.div
            animate={{ rotate: 360 }}
            transition={{ repeat: Infinity, duration: 20, ease: 'linear' }}
            style={{
              position: 'absolute',
              top: 0,
              left: 0,
              width: '100%',
              height: '100%',
              borderRadius: '50%',
              background: 'radial-gradient(circle at center, #8b5cf6, #4c1d95)',
              opacity: 0.7,
              filter: 'blur(100px)',
            }}
          />

          {/* WORKER CARD with pop-in animation on change */}
          <AnimatePresence mode="wait">
            {currentWorker && (
              <motion.div
                key={currentWorker._id} // ensure unique key for AnimatePresence
                initial={{ y: 50, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                exit={{ y: -50, opacity: 0 }}
                transition={{ duration: 0.6, ease: 'easeOut' }}
                style={{
                  position: 'absolute',
                  top: '50%',
                  left: '50%',
                  transform: 'translate(-50%, -50%) rotate(-8deg)',
                  width: '300px',
                  background: 'rgba(255, 255, 255, 0.05)',
                  border: '1px solid rgba(255, 255, 255, 0.2)',
                  borderRadius: '1rem',
                  padding: '1.5rem',
                  backdropFilter: 'blur(15px)',
                  WebkitBackdropFilter: 'blur(15px)',
                  color: 'white',
                  textAlign: 'center',
                  boxShadow: '0 8px 32px rgba(0,0,0,0.25)',
                  zIndex: 10,
                }}
              >
                {currentWorker.photo ? (
                  <img
                    src={`http://localhost:5000/uploads/${currentWorker.photo}`}
                    alt={currentWorker.name}
                    style={{
                      width: '80px',
                      height: '80px',
                      borderRadius: '50%',
                      objectFit: 'cover',
                      marginBottom: '1rem',
                      border: '2px solid #fff',
                    }}
                  />
                ) : (
                  <div
                    style={{
                      width: '80px',
                      height: '80px',
                      borderRadius: '50%',
                      backgroundColor: '#eee',
                      margin: '0 auto 1rem',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      color: '#555',
                    }}
                  >
                    No Photo
                  </div>
                )}
                <h3 style={{ margin: '0.5rem 0' }}>{currentWorker.name}</h3>
                <p>
                  <strong>Skill:</strong> {currentWorker.workSpecification || 'N/A'}
                </p>
                <p>
                  <strong>Location:</strong> {currentWorker.location || 'N/A'}
                </p>
                <p style={{ color: currentWorker.available ? '#2e7d32' : '#c62828' }}>
                  {currentWorker.available ? 'Available' : 'Booked'}
                </p>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </div>
    </div>
  );
};

export default HeroSection;
