import React, { useEffect, useState } from 'react';
import karnatakaDistrictsCoords from '../data/karnatakaDistrictsCoords';

const WorkersList = () => {
  const [workers, setWorkers] = useState([]);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [formData, setFormData] = useState({ location: '', doorNumber: '' });
  const [showInfo, setShowInfo] = useState(false);
  const [distance, setDistance] = useState('');
  const [arrivalTime, setArrivalTime] = useState('');
  const [bookedWorkerIds, setBookedWorkerIds] = useState([]);
  const [workerCoords, setWorkerCoords] = useState(null);
  const [showMap, setShowMap] = useState(false);

  useEffect(() => {
    const fetchWorkers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/workers');
        const data = await res.json();
        setWorkers(data);
      } catch (err) {
        console.error('Failed to fetch workers:', err);
      }
    };

    fetchWorkers();
  }, []);

  // Get lat/lng for district, fallback to Bengaluru if not found
  const getCoordsByDistrict = (district) => {
    if (district && karnatakaDistrictsCoords[district]) {
      return karnatakaDistrictsCoords[district];
    }
    // fallback coordinates for Bangalore Urban
    return { lat: 12.9716, lng: 77.5946 };
  };

  const handleTrack = (worker) => {
    // Assuming worker.location is the district name exactly matching keys in karnatakaDistrictsCoords
    const coords = getCoordsByDistrict(worker.location);
    setWorkerCoords(coords);
    setShowMap(true);
  };

  const handleBookNow = (worker) => {
    setSelectedWorker(worker);
    setFormData({ location: '', doorNumber: '' });
    setShowInfo(false);
    setDistance('');
    setArrivalTime('');
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    const updatedFormData = { ...formData, [name]: value };
    setFormData(updatedFormData);

    if (updatedFormData.location && updatedFormData.doorNumber) {
      const randomDistance = (Math.random() * (10 - 2) + 2).toFixed(2);
      const randomTime = Math.floor(Math.random() * (25 - 10) + 10);
      setDistance(`${randomDistance} km`);
      setArrivalTime(`${randomTime} minutes`);
      setShowInfo(true);
    }
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`http://localhost:5000/api/workers/${selectedWorker._id}/book`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      if (res.ok) {
        alert('Booking confirmed and email sent!');
        setWorkers((prev) =>
          prev.map((w) => (w._id === selectedWorker._id ? { ...w, available: false } : w))
        );
        setBookedWorkerIds((prev) => [...prev, selectedWorker._id]);
        setSelectedWorker(null); // Close the popup
        setFormData({ location: '', doorNumber: '' });

        setTimeout(() => {
          setWorkers((prev) =>
            prev.map((w) => (w._id === selectedWorker._id ? { ...w, available: true } : w))
          );
        }, 5 * 60 * 1000);
      } else {
        const errorData = await res.json();
        console.error('Booking error:', errorData);
        alert('Failed to book the worker.');
      }
    } catch (err) {
      console.error('Booking failed:', err);
      alert('Booking failed due to network/server error.');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '2rem' }}>Workers List</h2>
      {workers.length === 0 ? (
        <p style={{ textAlign: 'center' }}>No workers found.</p>
      ) : (
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
            gap: '1.5rem',
          }}
        >
          {workers.map((worker) => (
            <div
              key={worker._id}
              style={{
                border: '1px solid #ddd',
                borderRadius: '12px',
                padding: '1rem',
                boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
                textAlign: 'center',
              }}
            >
              {worker.photo ? (
                <img
                  src={`http://localhost:5000/uploads/${worker.photo}`}
                  alt={worker.name}
                  style={{
                    width: '100px',
                    height: '100px',
                    objectFit: 'cover',
                    borderRadius: '50%',
                    marginBottom: '1rem',
                  }}
                />
              ) : (
                <div
                  style={{
                    width: '100px',
                    height: '100px',
                    borderRadius: '50%',
                    backgroundColor: '#eee',
                    margin: '0 auto 1rem',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '14px',
                    color: '#888',
                  }}
                >
                  No Photo
                </div>
              )}
              <h3>{worker.name}</h3>
              <p>
                <strong>Work:</strong> {worker.workSpecification || 'N/A'}
              </p>
              <p>
                <strong>Experience:</strong> {worker.experience || 'N/A'}
              </p>
              <p>
                <strong>Location:</strong> {worker.location || 'N/A'}
              </p>
              <p>
                <strong>Phone:</strong> {worker.phone || 'N/A'}
              </p>
              <p>
                <strong>Status:</strong>{' '}
                <span style={{ color: worker.available ? 'green' : 'red' }}>
                  {worker.available ? 'Available' : 'Unavailable'}
                </span>
              </p>
              <button
                disabled={!worker.available}
                onClick={() => handleBookNow(worker)}
                style={{
                  padding: '0.5rem 1rem',
                  marginTop: '0.5rem',
                  backgroundColor: worker.available ? '#007bff' : '#999',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: worker.available ? 'pointer' : 'not-allowed',
                }}
              >
                Book Now
              </button>
              {bookedWorkerIds.includes(worker._id) && (
                <button
                  onClick={() => handleTrack(worker)}
                  style={{
                    marginTop: '0.5rem',
                    backgroundColor: '#28a745',
                    color: '#fff',
                    padding: '0.5rem 1rem',
                    border: 'none',
                    borderRadius: '6px',
                    cursor: 'pointer',
                  }}
                >
                  Track
                </button>
              )}
            </div>
          ))}
        </div>
      )}

      {selectedWorker && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100vw',
            height: '100vh',
            backgroundColor: 'rgba(0,0,0,0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 9999,
          }}
        >
          <div
            style={{
              backgroundColor: '#fff',
              padding: '2rem',
              borderRadius: '12px',
              width: '300px',
              position: 'relative',
            }}
          >
            <h3>Book {selectedWorker.name}</h3>
            <form onSubmit={handleFormSubmit}>
              <div style={{ marginBottom: '1rem' }}>
                <label htmlFor="location">Your Location (District):</label>
                <input
                  type="text"
                  id="location"
                  name="location"
                  value={formData.location}
                  onChange={handleInputChange}
                  placeholder="e.g. Bangalore Urban"
                  required
                  style={{ width: '100%', padding: '0.5rem', borderRadius: '6px', border: '1px solid #ccc' }}
                />
              </div>
              <div style={{ marginBottom: '1rem' }}>
                <label htmlFor="doorNumber">Door Number:</label>
                <input
                  type="text"
                  id="doorNumber"
                  name="doorNumber"
                  value={formData.doorNumber}
                  onChange={handleInputChange}
                  placeholder="Your door number"
                  required
                  style={{ width: '100%', padding: '0.5rem', borderRadius: '6px', border: '1px solid #ccc' }}
                />
              </div>

              {showInfo && (
                <div
                  style={{
                    marginBottom: '1rem',
                    padding: '0.5rem',
                    backgroundColor: '#e9ecef',
                    borderRadius: '6px',
                    fontSize: '0.9rem',
                  }}
                >
                  <p>Distance: {distance}</p>
                  <p>Estimated Arrival Time: {arrivalTime}</p>
                </div>
              )}

              <button
                type="submit"
                style={{
                  backgroundColor: '#007bff',
                  color: '#fff',
                  border: 'none',
                  padding: '0.5rem 1rem',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  width: '100%',
                }}
              >
                Confirm Booking
              </button>
            </form>

            <button
              onClick={() => setSelectedWorker(null)}
              style={{
                position: 'absolute',
                top: '10px',
                right: '10px',
                background: 'none',
                border: 'none',
                fontSize: '1.2rem',
                cursor: 'pointer',
              }}
              aria-label="Close booking form"
            >
              &times;
            </button>
          </div>
        </div>
      )}

      {showMap && workerCoords && (
  <div
    style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
      backgroundColor: 'rgba(0,0,0,0.7)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 9999,
    }}
  >
    <iframe
      title="Worker Location"
      width="90%"
      height="90%"
      frameBorder="0"
      scrolling="no"
      marginHeight="0"
      marginWidth="0"
      src={`https://maps.google.com/maps?q=${workerCoords.lat},${workerCoords.lng}&z=15&output=embed`}
      style={{ borderRadius: '12px', boxShadow: '0 0 15px rgba(0,0,0,0.5)' }}
    ></iframe>

    <button
      onClick={() => setShowMap(false)}
      style={{
        position: 'fixed',
        top: '20px',
        right: '20px',
        background: '#ff4d4d',
        border: 'none',
        borderRadius: '50%',
        width: '35px',
        height: '35px',
        color: '#fff',
        fontWeight: 'bold',
        fontSize: '1.5rem',
        cursor: 'pointer',
        zIndex: 10000,
      }}
      aria-label="Close map"
    >
      Ã—
    </button>
  </div>
)}

    </div>
  );
};

export default WorkersList;
