import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Message = ({ receiverId, onClose }) => {
  const [content, setContent] = useState('');
  const [senderId, setSenderId] = useState('');
  const [status, setStatus] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSenderId = async () => {
      const user = JSON.parse(localStorage.getItem('user'));

      if (user?.email) {
        try {
          const res = await axios.get(`http://localhost:5000/api/users/email/${user.email}`);
          setSenderId(res.data._id);
        } catch (err) {
          console.error('❌ Error fetching sender ID:', err.response?.data || err.message);
          setStatus('Failed to load sender information.');
        }
      } else {
        console.warn('❌ No user found in localStorage');
        setStatus('Please log in again.');
      }

      setLoading(false);
    };

    fetchSenderId();
  }, []);

  const sendMessage = async () => {
    if (!content.trim()) {
      setStatus('⚠️ Please enter a message.');
      return;
    }

    try {
      await axios.post('http://localhost:5000/api/messages', {
        senderId,
        receiverId,
        content,
      });

      setStatus('✅ Message sent!');
      setContent('');
    } catch (err) {
      console.error('❌ Message send failed:', err.response?.data || err.message);
      setStatus('❌ Failed to send message.');
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="message-popup" style={{ padding: 20, border: '1px solid #ccc', backgroundColor: '#fff' }}>
      <textarea
        placeholder="Type your message..."
        value={content}
        onChange={(e) => setContent(e.target.value)}
        rows={4}
        style={{ width: '100%', padding: '0.5rem' }}
      />
      <div style={{ marginTop: 10 }}>
        <button onClick={sendMessage} disabled={!senderId || loading}>
          Send
        </button>
        <button onClick={onClose} style={{ marginLeft: 10 }}>
          Close
        </button>
      </div>
      {status && <p style={{ marginTop: 10 }}>{status}</p>}
    </div>
  );
};

export default Message;
