import React, { useEffect, useState } from 'react';

const WorkersList = () => {
  const [workers, setWorkers] = useState([]);
  const [userCoords, setUserCoords] = useState(null);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [showInfo, setShowInfo] = useState(false);
  const [formData, setFormData] = useState({ doorNumber: '' });
  const [distance, setDistance] = useState('');
  const [arrivalTime, setArrivalTime] = useState('');
  const [showMap, setShowMap] = useState(false);
  const [workerCoords, setWorkerCoords] = useState(null);
  const [coordsError, setCoordsError] = useState(null);
  const [loadingCoords, setLoadingCoords] = useState(false);

  useEffect(() => {
    // Fetch workers list from backend
    fetch('/api/workers')
      .then(res => res.json())
      .then(data => setWorkers(data))
      .catch(err => console.error('Error fetching workers:', err));

    // Fetch logged-in user coordinates from backend
    fetch('/api/user/location')
      .then(res => res.json())
      .then(data => setUserCoords({ lat: data.lat, lng: data.lng }))
      .catch(err => console.error('Error fetching user location:', err));
  }, []);

  // Haversine formula to calculate distance between two lat/lng points in km
  const getDistance = (lat1, lon1, lat2, lon2) => {
    const toRad = deg => (deg * Math.PI) / 180;
    const R = 6371; // Earth radius in km
    const dLat = toRad(lat2 - lat1);
    const dLon = toRad(lon2 - lon1);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) *
      Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };

  // Handle booking button click: get worker coords by geocoding location string
  const handleBookClick = async (worker) => {
    if (!worker.location) {
      alert('Worker location not available.');
      return;
    }
    setLoadingCoords(true);
    try {
      // Geocode worker.location using OpenStreetMap Nominatim API
      const response = await fetch(
        `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(worker.location)}`
      );
      const results = await response.json();
      if (results.length === 0) {
        setCoordsError('Could not find coordinates for this location.');
        setLoadingCoords(false);
        return;
      }
      const lat = parseFloat(results[0].lat);
      const lng = parseFloat(results[0].lon);
      setWorkerCoords({ lat, lng });

      if (userCoords) {
        const dist = getDistance(userCoords.lat, userCoords.lng, lat, lng);
        setDistance(dist.toFixed(2) + ' km');

        // Estimate arrival time assuming 40 km/h speed
        const timeHours = dist / 40;
        const timeMinutes = Math.round(timeHours * 60);
        setArrivalTime(timeMinutes + ' minutes');
      }
      setSelectedWorker(worker);
      setShowInfo(true);
      setCoordsError(null);
    } catch (error) {
      setCoordsError('Error fetching coordinates.');
    } finally {
      setLoadingCoords(false);
    }
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Confirm booking submit handler
  const handleFormSubmit = (e) => {
    e.preventDefault();
    // Example: Send booking data to backend with doorNumber, user coords, worker id, etc.
    const bookingData = {
      workerId: selectedWorker._id,
      doorNumber: formData.doorNumber,
      userCoords,
    };
    console.log('Booking confirmed:', bookingData);

    // TODO: Implement backend API call to save booking here

    setShowInfo(false);
    setFormData({ doorNumber: '' });
    setSelectedWorker(null);
  };

  // Show the map popup
  const handleTrackClick = () => {
    if (!workerCoords || !userCoords) {
      alert('Location data not available for tracking.');
      return;
    }
    setShowMap(true);
  };

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Available Workers</h2>

      {workers.length === 0 ? (
        <p>No workers available at the moment.</p>
      ) : (
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
            gap: '1rem',
          }}
        >
          {workers.map(worker => (
            <div
              key={worker._id}
              style={{
                border: '1px solid #ddd',
                borderRadius: '8px',
                padding: '1rem',
                boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
              }}
            >
              <h3>{worker.name}</h3>
              <p><strong>Work:</strong> {worker.work}</p>
              <p><strong>Experience:</strong> {worker.experience} years</p>
              <p><strong>Location:</strong> {worker.location}</p>

              {!selectedWorker || selectedWorker._id !== worker._id ? (
                <button
                  onClick={() => handleBookClick(worker)}
                  style={{
                    backgroundColor: '#28a745',
                    color: 'white',
                    padding: '0.5rem 1rem',
                    border: 'none',
                    borderRadius: '6px',
                    cursor: 'pointer',
                  }}
                >
                  Book
                </button>
              ) : (
                <button
                  onClick={handleTrackClick}
                  style={{
                    backgroundColor: '#007bff',
                    color: 'white',
                    padding: '0.5rem 1rem',
                    border: 'none',
                    borderRadius: '6px',
                    cursor: 'pointer',
                    marginTop: '0.5rem',
                  }}
                >
                  Track
                </button>
              )}
            </div>
          ))}
        </div>
      )}

      {showInfo && selectedWorker && (
        <div
          style={{
            position: 'fixed',
            top: '15%',
            left: '50%',
            transform: 'translateX(-50%)',
            backgroundColor: 'white',
            padding: '2rem',
            borderRadius: '12px',
            boxShadow: '0 8px 24px rgba(0,0,0,0.3)',
            maxWidth: '400px',
            zIndex: 1000,
          }}
        >
          <h3>Booking {selectedWorker.name}</h3>

          <p>
            <strong>Estimated Distance:</strong> {distance}
          </p>
          <p>
            <strong>Estimated Arrival Time:</strong> {arrivalTime}
          </p>

          <form onSubmit={handleFormSubmit}>
            <label htmlFor="doorNumber">Door Number / Flat Number:</label>
            <input
              type="text"
              id="doorNumber"
              name="doorNumber"
              value={formData.doorNumber}
              onChange={handleInputChange}
              required
              style={{
                width: '100%',
                padding: '0.5rem',
                marginTop: '0.5rem',
                marginBottom: '1rem',
                borderRadius: '6px',
                border: '1px solid #ccc',
              }}
            />
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <button
                type="button"
                onClick={() => setShowInfo(false)}
                style={{
                  padding: '0.5rem 1rem',
                  borderRadius: '6px',
                  backgroundColor: '#6c757d',
                  color: 'white',
                  border: 'none',
                  cursor: 'pointer',
                }}
              >
                Cancel
              </button>
              <button
                type="submit"
                style={{
                  padding: '0.5rem 1rem',
                  borderRadius: '6px',
                  backgroundColor: '#007bff',
                  color: 'white',
                  border: 'none',
                  cursor: 'pointer',
                }}
              >
                Confirm Booking
              </button>
            </div>
          </form>
        </div>
      )}

      {showMap && workerCoords && userCoords && (
        <div
          style={{
            position: 'fixed',
            top: '10%',
            left: '50%',
            transform: 'translateX(-50%)',
            width: '600px',
            height: '400px',
            backgroundColor: 'white',
            borderRadius: '12px',
            boxShadow: '0 8px 24px rgba(0,0,0,0.3)',
            zIndex: 1000,
            padding: '1rem',
          }}
        >
          <h3 style={{ marginBottom: '1rem' }}>Track {selectedWorker?.name || 'Worker'}</h3>

          {coordsError && <p style={{ color: 'red' }}>{coordsError}</p>}

          {loadingCoords ? (
            <p>Loading map...</p>
          ) : (
            <iframe
              title="Map showing user and worker"
              width="100%"
              height="100%"
              frameBorder="0"
              scrolling="no"
              marginHeight="0"
              marginWidth="0"
              srcDoc={`
                <!DOCTYPE html>
                <html>
                  <head>
                    <meta charset="utf-8" />
                    <meta name="viewport" content="initial-scale=1.0, user-scalable=no" />
                    <style>html, body, #map { margin:0; padding:0; height:100%; }</style>
                    <link
                      rel="stylesheet"
                      href="https://unpkg.com/leaflet@1.9.3/dist/leaflet.css"
                      integrity="sha256-sA+u9I2FbYZtYWu5T+DRtqW45KxTkuJvUpzo7GZ9iIk="
                      crossorigin=""
                    />
                  </head>
                  <body>
                    <div id="map"></div>
                    <script src="https://unpkg.com/leaflet@1.9.3/dist/leaflet.js"></script>
                    <script>
                      var map = L.map('map').fitBounds([
                        [${userCoords.lat}, ${userCoords.lng}],
                        [${workerCoords.lat}, ${workerCoords.lng}]
                      ]);

                      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
                        maxZoom: 19,
                        attribution: '© OpenStreetMap contributors'
                      }).addTo(map);

                      L.marker([${userCoords.lat}, ${userCoords.lng}]).addTo(map).bindPopup('You (User)').openPopup();
                      L.marker([${workerCoords.lat}, ${workerCoords.lng}]).addTo(map).bindPopup('Worker').openPopup();
                    </script>
                  </body>
                </html>
              `}
            />
          )}

          <button
            onClick={() => {
              setShowMap(false);
              setWorkerCoords(null);
              setSelectedWorker(null);
              setCoordsError(null);
            }}
            style={{
              position: 'absolute',
              top: '10px',
              right: '10px',
              backgroundColor: '#dc3545',
              border: 'none',
              color: 'white',
              padding: '0.3rem 0.6rem',
              borderRadius: '6px',
              cursor: 'pointer',
              fontSize: '16px',
            }}
          >
            × Close
          </button>
        </div>
      )}
    </div>
  );
};

export default WorkersList;
