import React, { useEffect, useState } from 'react';

const Message = ({ sender, receiver }) => {
  const [messages, setMessages] = useState([]);
  const [content, setContent] = useState('');

  useEffect(() => {
    if (sender && receiver) {
      fetch(`http://localhost:5000/api/messages/${sender}/${receiver}`)
        .then(res => res.json())
        .then(data => {
          if (Array.isArray(data)) setMessages(data);
          else console.error('Invalid messages format:', data);
        });
    }
  }, [sender, receiver]);

  const handleSend = async () => {
    if (!content.trim()) return;

    if (!sender || !receiver) {
      alert("Sender or receiver is missing!");
      return;
    }

    try {
      const res = await fetch('http://localhost:5000/api/messages', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ sender, receiver, content }),
      });

      const newMsg = await res.json();
      if (res.ok) {
        setMessages(prev => [...prev, newMsg]);
        setContent('');
      } else {
        alert(newMsg.message || 'Failed to send message');
      }
    } catch (err) {
      console.error('Error sending message:', err);
      alert('Server error');
    }
  };

  return (
    <div style={{ padding: '1rem', background: '#f9f9f9', maxWidth: 600, margin: 'auto' }}>
      <h3>Chat with {receiver}</h3>
      <div style={{ maxHeight: '300px', overflowY: 'scroll', border: '1px solid #ccc', padding: '0.5rem' }}>
        {messages.map((msg, i) => (
          <div key={i} style={{ textAlign: msg.sender === sender ? 'right' : 'left' }}>
            <strong>{msg.sender}</strong>: {msg.content}
          </div>
        ))}
      </div>
      <input
        value={content}
        onChange={e => setContent(e.target.value)}
        placeholder="Type your message"
        style={{ width: '80%', marginTop: '1rem' }}
      />
      <button onClick={handleSend} style={{ marginLeft: '0.5rem' }}>Send</button>
    </div>
  );
};

export default Message;
