import React, { useEffect, useState } from 'react';
import {
  MapContainer,
  TileLayer,
  Marker,
  Polyline,
  Popup,
} from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix Leaflet's missing marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

const TrackWorker = ({ userLocation, workerId }) => {
  const [workerLocation, setWorkerLocation] = useState(null);

  useEffect(() => {
    const fetchLocation = async () => {
      try {
        if (!workerId) {
          console.warn("No workerId provided");
          return;
        }

        const res = await fetch(
          `http://localhost:5000/api/workers/${workerId}/location`
        );
        const data = await res.json();

        console.log("Fetched worker location:", data);

        if (data.latitude && data.longitude) {
          setWorkerLocation([parseFloat(data.latitude), parseFloat(data.longitude)]);
        } else {
          console.warn("Invalid latitude/longitude in response:", data);
        }
      } catch (err) {
        console.error('Error fetching worker location:', err);
      }
    };

    fetchLocation();
    const interval = setInterval(fetchLocation, 5000);

    return () => clearInterval(interval);
  }, [workerId]);

  const defaultCenter = userLocation || [12.9716, 77.5946];

  return (
    <div style={{ height: '400px', width: '100%', marginTop: '20px' }}>
      <MapContainer center={defaultCenter} zoom={13} style={{ height: '100%', width: '100%' }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {userLocation && (
          <Marker position={userLocation}>
            <Popup>Your Home</Popup>
          </Marker>
        )}

        {workerLocation && (
          <Marker position={workerLocation}>
            <Popup>Worker</Popup>
          </Marker>
        )}

        {userLocation && workerLocation && (
          <Polyline positions={[userLocation, workerLocation]} color="blue" />
        )}
      </MapContainer>
    </div>
  );
};

export default TrackWorker;
