import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const [showDropdown, setShowDropdown] = useState(false);

  const handleLogout = () => {
    logout();
    window.location.href = '/';
  };

  return (
    <nav style={styles.navbar}>
      <div style={styles.logo}>ðŸŒ¿FixItNow</div>
      <div style={{ position: 'relative' }}>
        <Link style={styles.link} to="/">Home</Link>
        <Link style={styles.link} to="/workerslist">Services</Link>

        {!user ? (
          <>
            <Link style={styles.link} to="/signup">Signup</Link>
            <Link style={styles.link} to="/login">Login</Link>
          </>
        ) : (
          <div
            style={styles.accountIcon}
            onClick={() => setShowDropdown(!showDropdown)}
          >
            ðŸ‘¤
            {showDropdown && (
              <div style={styles.dropdown}>
                <div style={styles.dropdownItem}>{user.username}</div>
                <div style={styles.dropdownItem} onClick={handleLogout}>Logout</div>
              </div>
            )}
          </div>
        )}
      </div>
    </nav>
  );
};

const styles = {
  navbar: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: '1rem 2rem',
    backgroundColor: '#111827',
    color: 'white',
    alignItems: 'center',
  },
  logo: {
    fontWeight: 'bold',
    fontSize: '1.5rem',
  },
  link: {
    margin: '0 1rem',
    color: 'white',
    textDecoration: 'none',
    fontWeight: '500',
  },
  accountIcon: {
    display: 'inline-block',
    marginLeft: '1rem',
    cursor: 'pointer',
    position: 'relative',
  },
  dropdown: {
    position: 'absolute',
    top: '2.5rem',
    right: 0,
    backgroundColor: '#fff',
  
    border: '1px solid #ccc',
    borderRadius: '5px',
    overflow: 'hidden',
  },
  dropdownItem: {
    padding: '0.5rem 1rem',
    cursor: 'pointer',
    borderBottom: '1px solid #eee',
  },
};

export default Navbar;
