import React from 'react';

const servicesData = [
  { id: 1, name: 'Plumber', description: 'Expert in fixing leaks, pipes, and water systems.' },
  { id: 2, name: 'Electrician', description: 'Certified to handle electrical wiring and repairs.' },
  { id: 3, name: 'Contractor', description: 'Oversees construction projects from start to finish.' },
  { id: 4, name: 'Painter', description: 'Specializes in painting and surface finishes.' },
  { id: 5, name: 'Welder', description: 'Expert in joining metal parts using heat.' },
  { id: 6, name: 'Mason', description: 'Handles bricklaying and stonework.' },
  { id: 7, name: 'Carpenter', description: 'Skilled in woodwork and furniture building.' },
  { id: 8, name: 'Mechanic', description: 'Repairs and maintains vehicles and machinery.' },
  // Add more...
];

const Services = () => {
  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Our Services</h2>
      <div style={styles.grid}>
        {servicesData.map(service => (
          <div key={service.id} style={styles.card}>
            <h3>{service.name}</h3>
            <p>{service.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '2rem',
    background: 'rgba(255, 255, 255, 0.05)',
    color: '',
  },
  heading: {
    textAlign: 'center',
    marginBottom: '1.5rem',
    textShadow: '1px 1px 2px #000',
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(4, 1fr)',
    gap: '1rem',
  },
  card: {
    background: 'rgba(255, 255, 255, 0.1)',
    padding: '1rem',
    borderRadius: '10px',
    textAlign: 'center',
    boxShadow: '0 2px 8px rgba(0,0,0,0.3)',
  },
};

export default Services;
