import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Default marker fix
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
});

const WorkersList = () => {
  const [workers, setWorkers] = useState([]);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [formData, setFormData] = useState({ location: '', doorNumber: '' });
  const [userCoords, setUserCoords] = useState(null);
  const [workerCoords, setWorkerCoords] = useState(null);
  const [showMap, setShowMap] = useState(false);
  const [bookedWorkerIds, setBookedWorkerIds] = useState([]);

  useEffect(() => {
    const fetchWorkers = async () => {
      const res = await fetch('http://localhost:5000/api/workers');
      const data = await res.json();
      setWorkers(data);
    };
    fetchWorkers();
  }, []);

  const fetchCoordsFromAddress = async (address) => {
    const url = `https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(
      address
    )}&format=json&limit=1`;
    const res = await fetch(url);
    const data = await res.json();
    if (data.length > 0) {
      return { lat: parseFloat(data[0].lat), lng: parseFloat(data[0].lon) };
    }
    return null;
  };

  const fetchUserLocation = async () => {
    const res = await fetch('http://localhost:5000/api/users/me'); // Replace with your endpoint
    const userData = await res.json();
    if (userData && userData.location) {
      return await fetchCoordsFromAddress(userData.location);
    }
    return null;
  };

  const handleTrack = async (worker) => {
    const workerLoc = await fetchCoordsFromAddress(worker.location);
    const userLoc = await fetchUserLocation();
    if (workerLoc && userLoc) {
      setWorkerCoords(workerLoc);
      setUserCoords(userLoc);
      setShowMap(true);
    } else {
      alert('Could not fetch location coordinates.');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2 style={{ textAlign: 'center' }}>Workers List</h2>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '1.5rem' }}>
        {workers.map((worker) => (
          <div key={worker._id} style={{ padding: '1rem', border: '1px solid #ddd', borderRadius: '12px' }}>
            <h3>{worker.name}</h3>
            <p><strong>Location:</strong> {worker.location}</p>
            <p><strong>Status:</strong> {worker.available ? 'Available' : 'Unavailable'}</p>
            <button
              disabled={!worker.available}
              onClick={() => setSelectedWorker(worker)}
              style={{ marginTop: '0.5rem' }}
            >
              Book Now
            </button>
            {bookedWorkerIds.includes(worker._id) && (
              <button
                onClick={() => handleTrack(worker)}
                style={{ marginTop: '0.5rem', background: 'green', color: '#fff' }}
              >
                Track
              </button>
            )}
          </div>
        ))}
      </div>

      {/* üó∫Ô∏è Map Popup */}
      {showMap && workerCoords && userCoords && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100vw',
            height: '100vh',
            backgroundColor: 'rgba(0,0,0,0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 1000,
          }}
          onClick={() => setShowMap(false)}
        >
          <div
            style={{
              width: '90vw',
              height: '70vh',
              backgroundColor: '#fff',
              borderRadius: '12px',
              overflow: 'hidden',
              position: 'relative',
            }}
            onClick={(e) => e.stopPropagation()}
          >
            <MapContainer
              center={workerCoords}
              zoom={13}
              style={{ height: '100%', width: '100%' }}
            >
              <TileLayer
                attribution='&copy; <a href="https://osm.org/copyright">OpenStreetMap</a>'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              />
              <Marker position={workerCoords}>
                <Popup>Worker Location</Popup>
              </Marker>
              <Marker position={userCoords}>
                <Popup>Your Home</Popup>
              </Marker>
            </MapContainer>
            <button
              onClick={() => setShowMap(false)}
              style={{
                position: 'absolute',
                top: 10,
                right: 10,
                background: 'red',
                color: 'white',
                border: 'none',
                borderRadius: '6px',
                padding: '0.5rem 1rem',
                cursor: 'pointer',
              }}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default WorkersList;
