import React, { useState } from 'react';

const servicesData = [
  { id: 1, name: 'Plumber', description: 'Expert in fixing leaks, pipes, and water systems.', email: 'plumber@example.com', available: true },
  { id: 2, name: 'Electrician', description: 'Certified to handle electrical wiring and repairs.', email: 'electrician@example.com', available: true },
  // ... other services
];

const Services = () => {
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [formData, setFormData] = useState({ location: '', doorNumber: '' });

  const handleBookClick = (worker) => {
    setSelectedWorker(worker);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const bookingInfo = {
      ...formData,
      workerId: selectedWorker.id,
      workerEmail: selectedWorker.email,
    };

    const res = await fetch('/api/book-worker', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(bookingInfo),
    });

    if (res.ok) {
      alert('Booking confirmed! Worker notified.');
      setSelectedWorker(null);
      setFormData({ location: '', doorNumber: '' });
    }
  };

  return (
    <div style={styles.section}>
      <h2 style={styles.title}>Our Services</h2>
      <div style={styles.grid}>
        {servicesData.map((service) => (
          <div key={service.id} style={styles.card}>
            <h3>{service.name}</h3>
            <p>{service.description}</p>
            <button onClick={() => handleBookClick(service)} style={styles.bookButton}>Book</button>
          </div>
        ))}
      </div>

      {selectedWorker && (
        <div style={styles.modal}>
          <h2>Book {selectedWorker.name}</h2>
          <p>{selectedWorker.description}</p>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Your Location"
              value={formData.location}
              onChange={(e) => setFormData({ ...formData, location: e.target.value })}
              required
            />
            <input
              type="text"
              placeholder="Door Number"
              value={formData.doorNumber}
              onChange={(e) => setFormData({ ...formData, doorNumber: e.target.value })}
              required
            />
            <p>Estimated Distance: 5.2 km</p>
            <p>Minimum Arrival Time: 15 mins</p>
            <button type="submit">Confirm Booking</button>
          </form>
          <button onClick={() => setSelectedWorker(null)}>Cancel</button>
        </div>
      )}
    </div>
  );
};

const styles = {
  section: {
    backgroundColor: '#1c1c1c',
    color: 'white',
    padding: '50px 20px',
    textAlign: 'center',
  },
  title: {
    fontSize: '2.5rem',
    marginBottom: '30px',
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(4, 1fr)',
    gap: '25px',
    maxWidth: '1400px',
    margin: '0 auto',
  },
  card: {
    backgroundColor: '#2a2a2a',
    padding: '20px',
    borderRadius: '12px',
    border: '1px solid #444',
    boxShadow: '0 4px 12px rgba(255, 255, 255, 0.08)',
  },
  bookButton: {
    marginTop: '10px',
    padding: '8px 16px',
    backgroundColor: '#00aaff',
    border: 'none',
    borderRadius: '6px',
    color: 'white',
    cursor: 'pointer',
  },
  modal: {
    backgroundColor: '#2a2a2a',
    color: 'white',
    padding: '20px',
    position: 'fixed',
    top: '20%',
    left: '30%',
    width: '40%',
    borderRadius: '12px',
    boxShadow: '0 8px 24px rgba(0,0,0,0.3)',
  },
};

export default Services;
