import React from 'react';
import bluecolar from './bluecolar.mp4'; // Import video relative to this file

const Home = () => {
  return (
    <div style={styles.container}>
      {/* Background Video */}
      <video autoPlay loop muted style={styles.backgroundVideo}>
        <source src={bluecolar} type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      {/* Content */}
      <h1>Welcome to My MERN Auth Project</h1>
      <p>Secure authentication using email verification.</p>
    </div>
  );
};

const styles = {
  container: {
    position: 'relative',
    height: '100',
    color: 'white',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    textShadow: '2px 2px 4px #000',
    overflow: 'hidden',
  },
  backgroundVideo: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    objectFit: 'cover',
    zIndex: -1,
  },
};

export default Home;
