import React from 'react';
import Services from '../components/Services';       
import WorkersList from '../components/WorkersList';
import bluecolar from './bluecolar.mp4';

const Home = () => {
  return (
    <div style={styles.container}>
      {/* Background Video */}
       <video autoPlay loop muted style={styles.backgroundVideo}>
       <source src={bluecolar} type="video/mp4" />

        Your browser does not support the video tag.
      </video>

      {/* Welcome Message */}
      <div style={styles.welcomeContent}>
        <h1>Empowering Blue-Collar Workers Everywhere</h1>
        <p>Connecting skilled workers with meaningful job opportunitiesâ€”fast, simple, and secure.</p>
      </div>

      {/* Services Section */}
      <services />

      {/* Workers List Section */}
      <workerlist />
    </div>
  );
};

const styles = {
  container: {
    position: 'relative',
    color: 'white',
    minHeight: '100vh',
    overflowX: 'hidden',
  },
  backgroundVideo: {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100vw',
    height: '100vh',
    objectFit: 'cover',
    zIndex: -1,
    filter: 'brightness(0.5)', // darken video for better text visibility
  },
  welcomeContent: {
    position: 'relative',
    paddingTop: '20vh',
    textAlign: 'center',
    textShadow: '2px 2px 4px #000',
  },
};
export default Home;
