import React, { useEffect, useState } from 'react';

const WorkersList = () => {
  const [workers, setWorkers] = useState([]);

  useEffect(() => {
    const fetchWorkers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/workers');
        const data = await res.json();
        setWorkers(data);
      } catch (err) {
        console.error('Failed to fetch workers:', err);
      }
    };

    fetchWorkers();
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '2rem' }}>Workers List</h2>
      {workers.length === 0 ? (
        <p style={{ textAlign: 'center' }}>No workers found.</p>
      ) : (
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
            gap: '1.5rem',
          }}
        >
          {workers.map((worker) => (
            <div
              key={worker._id}
              style={{
                border: '1px solid #ddd',
                borderRadius: '12px',
                padding: '1rem',
                boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
                textAlign: 'center',
              }}
            >
              {worker.photo ? (
                <img
                  src={`http://localhost:5000/uploads/${worker.photo}`}
                  alt={worker.name}
                  style={{
                    width: '100px',
                    height: '100px',
                    objectFit: 'cover',
                    borderRadius: '50%',
                    marginBottom: '1rem',
                  }}
                />
              ) : (
                <div
                  style={{
                    width: '100px',
                    height: '100px',
                    borderRadius: '50%',
                    backgroundColor: '#eee',
                    margin: '0 auto 1rem',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '14px',
                    color: '#888',
                  }}
                >
                  No Photo
                </div>
              )}
              <h3>{worker.name}</h3>
              <p><strong>Work:</strong> {worker.workSpecification || 'N/A'}</p>
              <p><strong>Experience:</strong> {worker.experience || 'N/A'}</p>
              <p><strong>Location:</strong> {worker.location || 'N/A'}</p>
              <p>
                <strong>Status:</strong>{' '}
                <span style={{ color: worker.available ? 'green' : 'red' }}>
                  {worker.available ? 'Available' : 'Unavailable'}
                </span>
              </p>
              <button
                disabled={!worker.available}
                style={{
                  padding: '0.5rem 1rem',
                  marginTop: '0.5rem',
                  backgroundColor: worker.available ? '#007bff' : '#ccc',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '8px',
                  cursor: worker.available ? 'pointer' : 'not-allowed',
                }}
              >
                Book Now
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default WorkersList;
