import React, { useState, useEffect } from 'react';

const HeroSection = () => {
  const [workersCount, setWorkersCount] = useState(0);
  const [usersCount, setUsersCount] = useState(0);

  useEffect(() => {
    const fetchCounts = async () => {
      try {
        const workersRes = await fetch('http://localhost:5000/api/workers');
        const workersData = await workersRes.json();
        setWorkersCount(workersData.length);

        const usersRes = await fetch('http://localhost:5000/api/users');
        const usersData = await usersRes.json();
        setUsersCount(usersData.length);
      } catch (err) {
        console.error('Failed to fetch counts:', err);
      }
    };
    fetchCounts();
  }, []);

  return (
    <div
      style={{
        width: '100vw',
        margin: 0,
        background: '#1c1c1c',
        minHeight: '90vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '2rem',
        color: 'white',
        fontFamily: 'Arial, sans-serif',
        overflow: 'hidden',
      }}
    >
      <div
        style={{
          width: '100%',
          maxWidth: '1300px',
          display: 'flex',
          flexDirection: 'row',
          justifyContent: 'space-between',
          alignItems: 'center',
          flexWrap: 'wrap',
        }}
      >
        {/* LEFT TEXT SECTION with animation */}
        <div style={{ maxWidth: '900px', zIndex: 10 }}>
          <h1
            style={{
              fontSize: '3rem',
              fontWeight: 800,
              lineHeight: '1.2',
              overflow: 'hidden',
              display: 'inline-block',
              animation: 'slideFromTop 5s ease-in-out infinite',
            }}
          >
            <span style={{ color: '#FDE047', fontSize: '3.5rem' }}>âœ¦</span>{' '}
            WORKER SPOTLIGHT
          </h1>

          <p
            style={{
              marginTop: '1rem',
              color: '#aaa',
              fontSize: '1.5rem',
              overflow: 'hidden',
              whiteSpace: 'nowrap',
              display: 'inline-block',
              animation: 'moveText 10s linear infinite',
            }}
          >
            Your trusted platform to find and book skilled workers instantly.
          </p>

          <style>
            {`
              @keyframes slideFromTop {
                0% { transform: translateY(-100%); opacity: 0; }
                50% { transform: translateY(0); opacity: 1; }
                100% { transform: translateY(-100%); opacity: 0; }
              }

              @keyframes moveText {
                0% { transform: translateX(100%); }
                100% { transform: translateX(-100%); }
              }
            `}
          </style>
        </div>

        {/* COUNTS SECTION */}
        <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem', alignItems: 'center' }}>
          <div style={{ background: '#333', padding: '1rem 2rem', borderRadius: '10px' }}>
            <h2 style={{ fontSize: '2rem', margin: 0 }}>{usersCount}</h2>
            <p style={{ margin: 0 }}>Total Users</p>
          </div>

          <div style={{ background: '#333', padding: '1rem 2rem', borderRadius: '10px' }}>
            <h2 style={{ fontSize: '2rem', margin: 0 }}>{workersCount}</h2>
            <p style={{ margin: 0 }}>Total Workers</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HeroSection;
