import React, { useState, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';

const Login = () => {
  const { login } = useContext(AuthContext);
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setMessage('');
    try {
      const res = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const data = await res.json();
      if (res.ok) {
        login(data.user); // Save to context
        window.location.href = '/';
      } else {
        setMessage(data.message || 'Login failed');
      }
    } catch {
      setMessage('Error connecting to server');
    }
  };

  return (
    <div style={styles.container}>
      <h2>Login</h2>
      <form onSubmit={handleLogin} style={styles.form}>
        <input name="email" placeholder="Email" value={formData.email} onChange={handleChange} required style={styles.input} />
        <input name="password" type="password" placeholder="Password" value={formData.password} onChange={handleChange} required style={styles.input} />
        <button type="submit" style={styles.button}>Login</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

const styles = {
  container: { maxWidth: '400px', margin: '2rem auto', padding: '2rem', boxShadow: '0 0 10px #ccc', borderRadius: '8px' },
  form: { display: 'flex', flexDirection: 'column' },
  input: { padding: '0.7rem', marginBottom: '1rem', fontSize: '1rem' },
  button: { padding: '0.7rem', backgroundColor: '#2c3e50', color: 'white', border: 'none', cursor: 'pointer' },
};

export default Login;
