// Save this file as WorkersList.js inside /client/src/pages

import React, { useEffect, useState } from 'react';

const WorkersList = () => {
  const [workers, setWorkers] = useState([]);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [formData, setFormData] = useState({ location: '', doorNumber: '' });

  useEffect(() => {
    fetch('http://localhost:5000/api/workers')
      .then(res => res.json())
      .then(data => setWorkers(data))
      .catch(err => console.error(err));
  }, []);

  const handleInputChange = e => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleBookNow = worker => {
    setSelectedWorker(worker);
    setFormData({ location: '', doorNumber: '' });
  };

  const handleSubmit = e => {
    e.preventDefault();
    navigator.geolocation.getCurrentPosition(async position => {
      const userLatitude = position.coords.latitude;
      const userLongitude = position.coords.longitude;

      try {
        const res = await fetch(`http://localhost:5000/api/workers/${selectedWorker._id}/book`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ ...formData, userLatitude, userLongitude }),
        });

        const data = await res.json();
        if (res.ok) {
          alert('Booking successful and email sent!');
          setWorkers(prev =>
            prev.map(w =>
              w._id === selectedWorker._id ? { ...w, available: false } : w
            )
          );
        } else {
          alert(data.error || 'Booking failed');
        }
      } catch (err) {
        console.error(err);
        alert('Server error');
      }
    }, () => {
      alert('Location access denied.');
    });
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Available Workers</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '1rem' }}>
        {workers.map(worker => (
          <div key={worker._id} style={{ border: '1px solid #ccc', padding: '1rem', width: '250px' }}>
            <h4>{worker.name}</h4>
            <p>Work: {worker.workSpecification}</p>
            <p>Experience: {worker.experience}</p>
            <p>Status: {worker.available ? '✅ Available' : '❌ Unavailable'}</p>
            <button
              onClick={() => handleBookNow(worker)}
              disabled={!worker.available}
            >
              Book Now
            </button>
          </div>
        ))}
      </div>

      {selectedWorker && (
        <div style={{
          position: 'fixed', top: '20%', left: '30%', background: '#fff',
          padding: '2rem', border: '1px solid #007bff', zIndex: 10
        }}>
          <h3>Booking {selectedWorker.name}</h3>
          <form onSubmit={handleSubmit}>
            <input
              name="location"
              placeholder="Your Location"
              value={formData.location}
              onChange={handleInputChange}
              required
              style={{ marginBottom: '1rem', width: '100%' }}
            />
            <input
              name="doorNumber"
              placeholder="Door Number"
              value={formData.doorNumber}
              onChange={handleInputChange}
              required
              style={{ marginBottom: '1rem', width: '100%' }}
            />
            <button type="submit">Confirm Booking</button>
            <button onClick={() => setSelectedWorker(null)} type="button" style={{ marginLeft: '1rem' }}>
              Cancel
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default WorkersList;
