import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';

// Fix default icon issue with Leaflet in React
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const TrackWorker = () => {
  const { workerId } = useParams();
  const navigate = useNavigate();

  const [worker, setWorker] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchWorker = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/workers/${workerId}`);
        if (!res.ok) throw new Error('Failed to fetch worker');
        const data = await res.json();
        setWorker(data);
      } catch (err) {
        console.error(err);
        alert('Could not load worker data');
        navigate('/'); // Redirect on error
      } finally {
        setLoading(false);
      }
    };
    fetchWorker();
  }, [workerId, navigate]);

  if (loading) return <p>Loading worker data...</p>;
  if (!worker) return <p>No worker data found.</p>;

  const { latitude, longitude, name } = worker;

  if (!latitude || !longitude) {
    return <p>Worker location not available.</p>;
  }

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Tracking Worker: {name}</h2>
      <button
        onClick={() => navigate(-1)}
        style={{
          marginBottom: '1rem',
          padding: '0.5rem 1rem',
          backgroundColor: '#007bff',
          color: '#fff',
          border: 'none',
          borderRadius: '6px',
          cursor: 'pointer',
        }}
      >
        Back
      </button>

      <MapContainer
        center={[latitude, longitude]}
        zoom={13}
        style={{ height: '500px', width: '100%', borderRadius: '12px' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; OpenStreetMap contributors"
        />
        <Marker position={[latitude, longitude]}>
          <Popup>{name}</Popup>
        </Marker>
      </MapContainer>
    </div>
  );
};

export default TrackWorker;
