// client/src/pages/Worker.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const Worker = () => {
  const { id } = useParams();
  const [worker, setWorker] = useState(null);
  const [status, setStatus] = useState('');

  useEffect(() => {
    const fetchWorker = async () => {
      const res = await fetch(`http://localhost:5000/api/workers/${id}`);
      const data = await res.json();
      setWorker(data);
    };
    fetchWorker();
  }, [id]);

  const handleBooking = async () => {
    const res = await fetch(`http://localhost:5000/api/workers/book/${id}`, {
      method: 'PATCH'
    });
    const data = await res.json();
    setStatus(data.message);
  };

  if (!worker) return <p>Loading...</p>;

  return (
    <div style={{ padding: 20 }}>
      <h2>{worker.name}</h2>
      <p><strong>Location:</strong> {worker.location}</p>
      <p><strong>Experience:</strong> {worker.experience} years</p>
      <p><strong>Rating:</strong> {worker.rating} / 5</p>
      <button onClick={handleBooking}>
        Book Now
      </button>
      {status && <p>{status}</p>}
    </div>
  );
};

export default Worker;
