import React, { useEffect, useState } from 'react';
import MapView from './MapView';

const WorkersList = () => {
  const [workers, setWorkers] = useState([]);
  const [formVisibleId, setFormVisibleId] = useState(null);
  const [formData, setFormData] = useState({
    location: '',
    doorNumber: '',
    userLatitude: '',
    userLongitude: '',
  });
  const [trackingWorkerId, setTrackingWorkerId] = useState(null);

  useEffect(() => {
    fetchWorkers();
    getUserLocation();
  }, []);

  const fetchWorkers = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/workers');
      const data = await res.json();
      setWorkers(data);
    } catch (err) {
      console.error('Error fetching workers:', err);
    }
  };

  const getUserLocation = () => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setFormData((prev) => ({
          ...prev,
          userLatitude: position.coords.latitude,
          userLongitude: position.coords.longitude,
        }));
      },
      (error) => {
        console.error('Error getting location:', error);
      }
    );
  };

  const handleBookClick = (workerId) => {
    setFormVisibleId(workerId);
  };

  const handleInputChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleFormSubmit = async (e, workerId) => {
    e.preventDefault();
    try {
      const res = await fetch(`http://localhost:5000/api/workers/${workerId}/book`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      const data = await res.json();
      alert(data.message);
      setFormVisibleId(null);
      fetchWorkers();
    } catch (err) {
      console.error('Booking error:', err);
      alert('Booking failed.');
    }
  };

  const handleTrackClick = (workerId) => {
    setTrackingWorkerId(workerId);
  };

  const handleCancelBooking = async (workerId) => {
    const confirmCancel = window.confirm('Are you sure you want to cancel this booking?');
    if (!confirmCancel) return;

    try {
      const res = await fetch(`http://localhost:5000/api/workers/${workerId}/cancel`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });

      const data = await res.json();

      if (res.ok) {
        alert(data.message);
        setTrackingWorkerId(null);
        fetchWorkers();
      } else {
        alert('Failed to cancel booking.');
      }
    } catch (err) {
      console.error('Cancel booking error:', err);
      alert('Error cancelling booking.');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2 style={{ textAlign: 'center' }}>Available Workers</h2>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '1rem' }}>
        {workers.map((worker) => (
          <div
            key={worker._id}
            style={{
              border: '1px solid #ccc',
              borderRadius: '10px',
              padding: '1rem',
              backgroundColor: '#f9f9f9',
            }}
          >
            <img
              src={worker.photo}
              alt={worker.name}
              style={{ width: '100%', height: '200px', objectFit: 'cover', borderRadius: '10px' }}
            />
            <h3>{worker.name}</h3>
            <p><strong>Specialization:</strong> {worker.work}</p>
            <p><strong>Experience:</strong> {worker.experience} years</p>
            <p><strong>Location:</strong> {worker.location}</p>
            <p><strong>Status:</strong> {worker.available ? 'Available' : 'Booked'}</p>

            {worker.available && (
              <button
                onClick={() => handleBookClick(worker._id)}
                style={{
                  padding: '0.4rem 1rem',
                  backgroundColor: '#007bff',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '8px',
                  cursor: 'pointer',
                }}
              >
                Book
              </button>
            )}

            {worker.booked && (
              <>
                <button
                  onClick={() => handleTrackClick(worker._id)}
                  style={{
                    padding: '0.4rem 1rem',
                    marginTop: '0.5rem',
                    marginRight: '0.5rem',
                    backgroundColor: '#28a745',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '8px',
                    cursor: 'pointer',
                  }}
                >
                  Track
                </button>
                <button
                  onClick={() => handleCancelBooking(worker._id)}
                  style={{
                    padding: '0.4rem 1rem',
                    marginTop: '0.5rem',
                    backgroundColor: '#dc3545',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '8px',
                    cursor: 'pointer',
                  }}
                >
                  Cancel
                </button>
              </>
            )}

            {formVisibleId === worker._id && (
              <form onSubmit={(e) => handleFormSubmit(e, worker._id)} style={{ marginTop: '1rem' }}>
                <input
                  type="text"
                  name="location"
                  placeholder="Your Location"
                  value={formData.location}
                  onChange={handleInputChange}
                  required
                  style={{ marginBottom: '0.5rem', width: '100%' }}
                />
                <input
                  type="text"
                  name="doorNumber"
                  placeholder="Door Number"
                  value={formData.doorNumber}
                  onChange={handleInputChange}
                  required
                  style={{ marginBottom: '0.5rem', width: '100%' }}
                />
                <button
                  type="submit"
                  style={{
                    padding: '0.4rem 1rem',
                    backgroundColor: '#28a745',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '8px',
                    cursor: 'pointer',
                  }}
                >
                  Confirm Booking
                </button>
              </form>
            )}

            {trackingWorkerId === worker._id && (
              <MapView latitude={worker.latitude} longitude={worker.longitude} />
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default WorkersList;
