import React, { useEffect, useState } from 'react';
import {
  MapContainer,
  TileLayer,
  Marker,
  Popup,
} from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix Leaflet's missing marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

const TrackWorker = ({ workerId }) => {
  const [workerLocation, setWorkerLocation] = useState(null);

  useEffect(() => {
    const fetchLocation = async () => {
      try {
        if (!workerId) {
          console.warn('No workerId provided');
          return;
        }

        const res = await fetch(`http://localhost:5000/api/workers/${workerId}/location`);
        const data = await res.json();

        const lat = parseFloat(data.latitude);
        const lon = parseFloat(data.longitude);

        if (!isNaN(lat) && !isNaN(lon)) {
          setWorkerLocation([lat, lon]);
        } else {
          console.warn('Invalid location, fallback to test location');
          setWorkerLocation([12.9716, 77.5946]); // Default Bangalore
        }
      } catch (err) {
        console.error('Error fetching worker location:', err);
      }
    };

    fetchLocation();
    const interval = setInterval(fetchLocation, 5000);
    return () => clearInterval(interval);
  }, [workerId]);

  if (!workerLocation) {
    return <p style={{ textAlign: 'center' }}>Fetching worker location...</p>;
  }

  return (
    <div style={{ height: '400px', width: '100%', marginTop: '20px' }}>
      <MapContainer center={workerLocation} zoom={15} style={{ height: '100%', width: '100%' }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        <Marker position={workerLocation}>
          <Popup>Worker Location</Popup>
        </Marker>
      </MapContainer>
    </div>
  );
};

export default TrackWorker;
