import React, { useState } from 'react';

const Message = ({ worker, onClose }) => {
  const [message, setMessage] = useState('');
  const [sentMessages, setSentMessages] = useState([]);

  const handleSend = () => {
    if (!message.trim()) return;
    setSentMessages([...sentMessages, { text: message, from: 'user' }]);
    setMessage('');
  };

  return (
    <div style={{ padding: '20px', border: '1px solid #ccc', marginTop: '20px' }}>
      <h3>Chat with {worker.name}</h3>
      <div style={{ maxHeight: '200px', overflowY: 'auto', marginBottom: '10px' }}>
        {sentMessages.map((msg, idx) => (
          <div key={idx} style={{ textAlign: msg.from === 'user' ? 'right' : 'left' }}>
            <p style={{
              display: 'inline-block',
              padding: '10px',
              backgroundColor: '#f1f1f1',
              borderRadius: '8px',
              margin: '5px 0'
            }}>
              {msg.text}
            </p>
          </div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        placeholder="Type your message..."
        onChange={(e) => setMessage(e.target.value)}
        style={{ width: '70%', padding: '10px' }}
      />
      <button onClick={handleSend} style={{ padding: '10px', marginLeft: '10px' }}>Send</button>
      <button onClick={onClose} style={{ padding: '10px', marginLeft: '10px', backgroundColor: 'red', color: 'white' }}>Close</button>
    </div>
  );
};

export default Message;
