import React, { useEffect, useState } from 'react';
import TrackWorker from './TrackWorker';

const WorkersList = () => {
  const [workers, setWorkers] = useState([]);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [formData, setFormData] = useState({ location: '', doorNumber: '' });
  const [distance, setDistance] = useState('');
  const [arrivalTime, setArrivalTime] = useState('');
  const [bookedWorkerIds, setBookedWorkerIds] = useState([]);
  const [userCoords, setUserCoords] = useState(null);
  const [showMap, setShowMap] = useState(false);
  const [showBookingPopup, setShowBookingPopup] = useState(false);
  const [messageContent, setMessageContent] = useState('');
  const [showMessageBox, setShowMessageBox] = useState(false);
  const [currentUser, setCurrentUser] = useState(null); // Logged in user

  useEffect(() => {
    const fetchWorkers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/workers');
        const data = await res.json();
        setWorkers(data);
      } catch (err) {
        console.error('Failed to fetch workers:', err);
      }
    };

    const fetchUserLocation = async () => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            setUserCoords({
              lat: position.coords.latitude,
              lng: position.coords.longitude
            });
          },
          (error) => console.error('Geolocation error:', error)
        );
      }
    };

    const getCurrentUser = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/auth/me', {
          credentials: 'include'
        });
        const data = await res.json();
        setCurrentUser(data);
      } catch (error) {
        console.error('Error fetching current user:', error);
      }
    };

    fetchWorkers();
    fetchUserLocation();
    getCurrentUser();
  }, []);

  useEffect(() => {
    document.body.style.overflow = showMap ? 'hidden' : 'auto';
  }, [showMap]);

  const handleBookClick = (worker) => {
    setSelectedWorker(worker);
    setFormData({ location: '', doorNumber: '' });
    setDistance('');
    setArrivalTime('');
    setShowBookingPopup(true);
    setShowMap(false);
  };

  const handleBookingFormChange = (e) => {
    const { name, value } = e.target;
    const updatedFormData = { ...formData, [name]: value };
    setFormData(updatedFormData);

    if (updatedFormData.location && updatedFormData.doorNumber) {
      const randomDistance = (Math.random() * (10 - 2) + 2).toFixed(2);
      const randomTime = Math.floor(Math.random() * (25 - 10) + 10);
      setDistance(`${randomDistance} km`);
      setArrivalTime(`${randomTime} minutes`);
    } else {
      setDistance('');
      setArrivalTime('');
    }
  };

  const handleConfirmBooking = async () => {
    if (!formData.location || !formData.doorNumber) {
      alert('Please enter location and door number.');
      return;
    }
    try {
      const res = await fetch(`http://localhost:5000/api/workers/${selectedWorker._id}/book`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      if (res.ok) {
        alert('Booking confirmed!');
        setWorkers((prev) =>
          prev.map((w) => (w._id === selectedWorker._id ? { ...w, available: false } : w))
        );
        setBookedWorkerIds((prev) => [...prev, selectedWorker._id]);
        setShowBookingPopup(false);
        setTimeout(() => {
          setWorkers((prev) =>
            prev.map((w) => (w._id === selectedWorker._id ? { ...w, available: true } : w))
          );
          setBookedWorkerIds((prev) => prev.filter(id => id !== selectedWorker._id));
        }, 5 * 60 * 1000);
      }
    } catch (err) {
      alert('Booking failed. Please try again.');
    }
  };

  const handleSendMessage = async () => {
    if (!messageContent.trim()) return alert('Message cannot be empty!');
    try {
      const response = await fetch('http://localhost:5000/api/messages', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          sender: currentUser?.email,
          receiver: selectedWorker?.email,
          content: messageContent
        })
      });
      if (response.ok) {
        alert('Message sent!');
        setMessageContent('');
        setShowMessageBox(false);
      } else {
        alert('Failed to send message.');
      }
    } catch (err) {
      console.error('Error sending message:', err);
    }
  };

  const handleContact = () => {
    if (selectedWorker?.phoneNumber) {
      const telLink = `tel:${selectedWorker.phoneNumber}`;
      window.open(telLink, '_self');
    } else {
      alert('Phone number not available.');
    }
  };

  const handleClosePopup = () => {
    setShowBookingPopup(false);
    setShowMap(false);
    setShowMessageBox(false);
  };

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <h2 style={{ textAlign: 'center', margin: '20px 0' }}>Available Workers</h2>

      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
        gap: '20px'
      }}>
        {workers.map((worker) => (
          <div key={worker._id} style={{
            border: '1px solid #ddd',
            borderRadius: '10px',
            padding: '15px',
            boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
          }}>
            <div style={{ textAlign: 'center' }}>
              {worker.photo ? (
                <img
                  src={`http://localhost:5000/uploads/${worker.photo}`}
                  alt={worker.name}
                  style={{
                    width: '100px',
                    height: '100px',
                    borderRadius: '50%',
                    objectFit: 'cover',
                    border: '2px solid #eee'
                  }}
                />
              ) : (
                <div style={{
                  width: '100px',
                  height: '100px',
                  borderRadius: '50%',
                  backgroundColor: '#f0f0f0',
                  margin: '0 auto',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  color: '#888'
                }}>
                  No Photo
                </div>
              )}
              <h3 style={{ margin: '10px 0 5px' }}>{worker.name}</h3>
              <p><strong>Skill:</strong> {worker.workSpecification || 'N/A'}</p>
              <p><strong>Location:</strong> {worker.location || 'N/A'}</p>
              <p style={{ color: worker.available ? 'green' : 'red' }}>
                {worker.available ? 'Available' : 'Booked'}
              </p>
              <button
                onClick={() => handleBookClick(worker)}
                disabled={!worker.available}
                style={{
                  padding: '8px 15px',
                  backgroundColor: worker.available ? '#4CAF50' : '#cccccc',
                  color: 'white',
                  border: 'none',
                  borderRadius: '5px',
                  cursor: worker.available ? 'pointer' : 'not-allowed'
                }}
              >
                {worker.available ? 'Book Now' : 'Unavailable'}
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Booking Popup */}
      {showBookingPopup && selectedWorker && (
        <div style={{
          position: 'fixed',
          top: 0, left: 0, width: '100vw', height: '100vh',
          backgroundColor: 'rgba(0,0,0,0.6)',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          zIndex: 1000
        }}>
          <div style={{
            backgroundColor: 'white',
            borderRadius: '10px',
            padding: '20px',
            width: '100vw',
            height: '100vh',
            overflow: 'auto',
            position: 'relative'
          }}>
            <button onClick={handleClosePopup}
              style={{
                position: 'absolute', top: '10px', right: '10px',
                background: 'none', border: 'none', fontSize: '24px',
                cursor: 'pointer'
              }}>×</button>

            <div style={{ textAlign: 'center', marginBottom: '20px' }}>
              <h3>{selectedWorker.name}</h3>
              <p>{selectedWorker.email}</p>
            </div>

            <input
              type="text"
              placeholder="Your location"
              name="location"
              value={formData.location}
              onChange={handleBookingFormChange}
              style={{ width: '100%', marginBottom: '10px', padding: '10px' }}
            />
            <input
              type="text"
              placeholder="Door number"
              name="doorNumber"
              value={formData.doorNumber}
              onChange={handleBookingFormChange}
              style={{ width: '100%', marginBottom: '10px', padding: '10px' }}
            />
            {distance && arrivalTime && (
              <div>
                <p><b>Distance:</b> {distance}</p>
                <p><b>Arrival Time:</b> {arrivalTime}</p>
              </div>
            )}

            <div style={{ marginTop: '20px', display: 'flex', gap: '10px' }}>
              <button onClick={handleConfirmBooking} style={{ flex: 1, padding: '10px', backgroundColor: '#4CAF50', color: 'white' }}>Confirm Booking</button>
              <button onClick={() => setShowMessageBox(true)} style={{ flex: 1, padding: '10px', backgroundColor: 'blue', color: 'white' }}>Message</button>
              <button onClick={() => setShowMap(true)} style={{ flex: 1, padding: '10px', backgroundColor: '#2196F3', color: 'white' }}>View Location</button>
              <button onClick={handleContact} style={{ flex: 1, padding: '10px', backgroundColor: '#FF9800', color: 'white' }}>Call Worker</button>
            </div>

            {showMessageBox && (
              <div style={{ marginTop: '20px' }}>
                <textarea
                  rows="4"
                  style={{ width: '100%', padding: '10px' }}
                  placeholder="Write your message..."
                  value={messageContent}
                  onChange={(e) => setMessageContent(e.target.value)}
                />
                <button
                  onClick={handleSendMessage}
                  style={{ marginTop: '10px', padding: '10px', backgroundColor: 'green', color: 'white' }}>
                  Send Message
                </button>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Map Overlay */}
      {showMap && (
        <div style={{
          position: 'fixed', top: 0, left: 0, width: '100%', height: '100%',
          backgroundColor: '#fff', zIndex: 2000, display: 'flex', flexDirection: 'column',
        }}>
          <button
            onClick={() => setShowMap(false)}
            style={{
              padding: '10px', backgroundColor: '#f44336', color: 'white',
              border: 'none', fontSize: '18px', cursor: 'pointer'
            }}>
            ×
          </button>
          <div style={{ flex: 1 }}>
            <TrackWorker worker={selectedWorker} userCoords={userCoords} />
          </div>
        </div>
      )}
    </div>
  );
};

export default WorkersList;
