import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Inbox = ({ currentUserId }) => {
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const fetchInbox = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/messages/inbox/${currentUserId}`);
        setMessages(response.data);
      } catch (error) {
        console.error('Error fetching inbox:', error);
      }
    };

    fetchInbox();
  }, [currentUserId]);

  return (
    <div style={{ padding: '20px' }}>
      <h2>Your Inbox</h2>
      {messages.length === 0 ? (
        <p>No messages yet.</p>
      ) : (
        messages.map((msg) => (
          <div key={msg._id} style={{ borderBottom: '1px solid #ccc', padding: '10px 0' }}>
            <p><strong>From:</strong> {msg.senderId.name} ({msg.senderId.email})</p>
            <p><strong>Message:</strong> {msg.content}</p>
            <p><small>{new Date(msg.timestamp).toLocaleString()}</small></p>
          </div>
        ))
      )}
    </div>
  );
};

export default Inbox;
