import React from 'react';

const images = [
  'https://tse4.mm.bing.net/th?id=OIP.iUCmSM_vzFgXDE7zlIMGMQHaE8&pid=Api&P=0&h=180',
  'https://tse1.mm.bing.net/th?id=OIP.lAAU4-KxOhm1mbhd0mcLVQHaE8&pid=Api&P=0&h=180',
  'https://tse1.mm.bing.net/th?id=OIP._o6UwhbNH7sN2ngC3o7oWQHaE8&pid=Api&P=0&h=180',
  'https://tse4.mm.bing.net/th?id=OIP.NUFl7HUxP8yN6SxKlVF0eQHaEo&pid=Api&P=0&h=180',
  'https://tse4.mm.bing.net/th?id=OIP.npxSXzzm0eqq4u7YiPVSHwHaE8&pid=Api&P=0&h=180',
];

const WorkerShowcase = () => {
  const animationStyles = `
    @keyframes scrollDown {
      0% { transform: translateY(-100%); }
      100% { transform: translateY(0%); }
    }

    @keyframes scrollUp {
      0% { transform: translateY(0%); }
      100% { transform: translateY(-100%); }
    }
  `;

  const containerStyle = {
    display: 'flex',
    alignItems: 'center',
    backgroundColor: '#1c1c1c',
    padding: '3rem',
    gap: '4rem',
    color: 'white',
  };

  const scrollContainerStyle = {
    display: 'flex',
    gap: '1.5rem',
    height: '400px',
    overflow: 'hidden',
  };

  const columnStyle = {
    display: 'flex',
    flexDirection: 'column',
    gap: '1rem',
  };

  const leftColumnStyle = {
    ...columnStyle,
    animation: 'scrollDown 15s linear infinite',
  };

  const rightColumnStyle = {
    ...columnStyle,
    animation: 'scrollUp 15s linear infinite',
  };

  const imgStyle = {
    width: '160px',
    height: '160px',
    objectFit: 'cover',
    borderRadius: '10px',
    boxShadow: '0 4px 12px rgba(0,0,0,0.3)',
  };

  const textSectionStyle = {
    maxWidth: '400px',
  };

  return (
    <>
      <style>{animationStyles}</style>
      <div style={containerStyle}>
        {/* Left - Image Scroller */}
        <div style={scrollContainerStyle}>
          <div style={leftColumnStyle}>
            {[...images, ...images].map((src, i) => (
              <img key={`left-${i}`} src={src} alt="worker" style={imgStyle} />
            ))}
          </div>
          <div style={rightColumnStyle}>
            {[...images, ...images].map((src, i) => (
              <img key={`right-${i}`} src={src} alt="worker" style={imgStyle} />
            ))}
          </div>
        </div>

        {/* Right - Text */}
        <div style={textSectionStyle}>
          <h2 style={{ fontSize: '2rem', marginBottom: '1rem' }}>
            Empowering Local Talent
          </h2>
          <p style={{ fontSize: '1.1rem', lineHeight: '1.6' }}>
            Whether you’re a skilled technician, factory worker, or delivery
            expert — we help you connect with opportunities that match your
            skills, close to where you live.
          </p>
          <p style={{ marginTop: '1rem', color: '#bbb' }}>
            Build your future with us — one job at a time.
          </p>
        </div>
      </div>
    </>
  );
};

export default WorkerShowcase;
