import React, { useEffect, useState } from 'react';

const Message = ({ userId, workerId, onClose }) => {
  const [content, setContent] = useState('');
  const [senderEmail, setSenderEmail] = useState('');
  const [receiverEmail, setReceiverEmail] = useState('');

  // Fetch sender (user) and receiver (worker) info from backend
  useEffect(() => {
    const fetchDetails = async () => {
      try {
        // Fetch user
        const userRes = await fetch(`http://localhost:5000/api/users/${userId}`);
        const userData = await userRes.json();
        if (userRes.ok) {
          setSenderEmail(userData.email);
        } else {
          console.error('Failed to fetch user info');
        }

        // Fetch worker
        const workerRes = await fetch(`http://localhost:5000/api/workers/${workerId}`);
        const workerData = await workerRes.json();
        if (workerRes.ok) {
          setReceiverEmail(workerData.email);
        } else {
          console.error('Failed to fetch worker info');
        }
      } catch (error) {
        console.error('Error fetching sender/receiver info:', error);
      }
    };

    fetchDetails();
  }, [userId, workerId]);

  const handleSend = async (e) => {
    e.preventDefault();

    if (!content.trim()) {
      alert('Message cannot be empty');
      return;
    }

    try {
      const res = await fetch('http://localhost:5000/api/messages', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          sender: senderEmail,
          receiver: receiverEmail,
          content,
        }),
      });

      const data = await res.json();

      if (res.ok) {
        alert('Message sent!');
        setContent('');
        onClose(); // close popup
      } else {
        alert(`Failed to send message: ${data.error || 'Unknown error'}`);
      }
    } catch (err) {
      console.error('Send message error:', err);
      alert('Something went wrong');
    }
  };

  return (
    <div
      style={{
        position: 'fixed',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        backgroundColor: '#fff',
        padding: '20px',
        borderRadius: '10px',
        boxShadow: '0 4px 10px rgba(0,0,0,0.3)',
        zIndex: 3000,
        width: '300px',
      }}
    >
      <button
        onClick={onClose}
        style={{
          position: 'absolute',
          top: '10px',
          right: '15px',
          background: 'none',
          border: 'none',
          fontSize: '20px',
          cursor: 'pointer',
        }}
      >
        Ã—
      </button>
      <h3>Send a Message</h3>
      <form onSubmit={handleSend}>
        <textarea
          rows="4"
          style={{ width: '100%', padding: '10px', borderRadius: '5px' }}
          value={content}
          onChange={(e) => setContent(e.target.value)}
          placeholder="Type your message..."
        />
        <button
          type="submit"
          style={{
            marginTop: '10px',
            width: '100%',
            padding: '10px',
            backgroundColor: '#4CAF50',
            color: '#fff',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
          }}
        >
          Send
        </button>
      </form>
    </div>
  );
};

export default Message;
