const TrackWorker = ({ bookingId, workerId }) => {
  const [userLocation, setUserLocation] = useState(null);
  const [workerLocation, setWorkerLocation] = useState(null);

  useEffect(() => {
    const fetchUserLocation = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/bookings/${bookingId}/location`);
        const data = await res.json();
        if (data.latitude && data.longitude) {
          setUserLocation([parseFloat(data.latitude), parseFloat(data.longitude)]);
        }
      } catch (err) {
        console.error('Error fetching user location:', err);
      }
    };

    const fetchWorkerLocation = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/workers/${workerId}/location`);
        const data = await res.json();
        if (data.latitude && data.longitude) {
          setWorkerLocation([parseFloat(data.latitude), parseFloat(data.longitude)]);
        }
      } catch (err) {
        console.error('Error fetching worker location:', err);
      }
    };

    fetchUserLocation();
    fetchWorkerLocation();
    const interval = setInterval(fetchWorkerLocation, 5000);

    return () => clearInterval(interval);
  }, [bookingId, workerId]);

  const defaultCenter = userLocation || [12.9716, 77.5946];

  return (
    <div style={{ height: '400px', width: '100%', marginTop: '20px' }}>
      <MapContainer center={defaultCenter} zoom={13} style={{ height: '100%', width: '100%' }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {userLocation && (
          <Marker position={userLocation}>
            <Popup>Your Home</Popup>
          </Marker>
        )}

        {workerLocation && (
          <Marker position={workerLocation}>
            <Popup>Worker</Popup>
          </Marker>
        )}

        {userLocation && workerLocation && (
          <Polyline positions={[userLocation, workerLocation]} color="blue" />
        )}
      </MapContainer>
    </div>
  );
};
