import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

// To fix default marker icon issues in Leaflet with React, add these:
import L from 'leaflet';
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

const TrackWorker = () => {
  const { workerId } = useParams();
  const [worker, setWorker] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWorker = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/workers/${workerId}`);
        if (!res.ok) throw new Error('Worker not found');
        const data = await res.json();
        setWorker(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchWorker();
  }, [workerId]);

  if (loading) return <p>Loading worker location...</p>;
  if (error) return <p>Error: {error}</p>;
  if (!worker || !worker.latitude || !worker.longitude)
    return <p>Location data not available for this worker.</p>;

  const position = [worker.latitude, worker.longitude];

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Tracking: {worker.name}</h2>
      <p><strong>Phone:</strong> {worker.phone}</p>
      <p><strong>Location:</strong> {worker.location}</p>

      <MapContainer center={position} zoom={13} style={{ height: '400px', width: '100%' }}>
        <TileLayer
          attribution='&copy; <a href="https://osm.org/copyright">OpenStreetMap</a>'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Marker position={position}>
          <Popup>
            {worker.name} <br /> {worker.location}
          </Popup>
        </Marker>
      </MapContainer>
    </div>
  );
};

export default TrackWorker;
