import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../context/AuthContext';

const Inbox = () => {
  const { user } = useContext(AuthContext);
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    if (user?.role === 'worker') {
      fetch(`http://localhost:5000/api/messages/receiver/${user.email}`)
        .then(res => res.json())
        .then(data => setMessages(data))
        .catch(err => console.error(err));
    }
  }, [user]);

  return (
    <div style={{ padding: '2rem', maxWidth: '600px', margin: 'auto' }}>
      <h2>ðŸ“¥ Inbox</h2>
      {messages.length === 0 ? (
        <p>No messages yet.</p>
      ) : (
        messages.map((msg, index) => (
          <div key={index} style={{ marginBottom: '1rem', padding: '1rem', border: '1px solid #ccc', borderRadius: '5px' }}>
            <p><strong>From:</strong> {msg.sender}</p>
            <p>{msg.content}</p>
            <p style={{ fontSize: '0.8rem', color: 'gray' }}>{new Date(msg.createdAt).toLocaleString()}</p>
          </div>
        ))
      )}
    </div>
  );
};

export default Inbox;
