import React, { useEffect, useState } from 'react';
import TrackWorker from './TrackWorker';

const WorkersList = () => {
  const [workers, setWorkers] = useState([]);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [formData, setFormData] = useState({ location: '', doorNumber: '' });
  const [distance, setDistance] = useState('');
  const [arrivalTime, setArrivalTime] = useState('');
  const [bookedWorkerIds, setBookedWorkerIds] = useState([]);
  const [userCoords, setUserCoords] = useState(null);
  const [showMap, setShowMap] = useState(false);
  const [showBookingPopup, setShowBookingPopup] = useState(false);
  const [workerCoords, setWorkerCoords] = useState(null); // NEW: store worker coordinates

  useEffect(() => {
    const fetchWorkers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/workers');
        const data = await res.json();
        setWorkers(data);
      } catch (err) {
        console.error('Failed to fetch workers:', err);
      }
    };

    const fetchUserLocation = async () => {
      try {
        if (navigator.geolocation) {
          navigator.geolocation.getCurrentPosition(
            (position) => {
              setUserCoords({
                lat: position.coords.latitude,
                lng: position.coords.longitude
              });
            },
            (error) => console.error('Geolocation error:', error)
          );
        }
      } catch (err) {
        console.error('Failed to fetch user location:', err);
      }
    };

    fetchWorkers();
    fetchUserLocation();
  }, []);

  const handleBookClick = (worker) => {
    setSelectedWorker(worker);
    setFormData({ location: '', doorNumber: '' });
    setDistance('');
    setArrivalTime('');
    setShowBookingPopup(true);
    setShowMap(false); // Reset map visibility when reopening popup
  };

  const handleBookingFormChange = (e) => {
    const { name, value } = e.target;
    const updatedFormData = { ...formData, [name]: value };
    setFormData(updatedFormData);

    if (updatedFormData.location && updatedFormData.doorNumber) {
      const randomDistance = (Math.random() * (10 - 2) + 2).toFixed(2);
      const randomTime = Math.floor(Math.random() * (25 - 10) + 10);
      setDistance(`${randomDistance} km`);
      setArrivalTime(`${randomTime} minutes`);
    } else {
      setDistance('');
      setArrivalTime('');
    }
  };

  const handleConfirmBooking = async () => {
    if (!formData.location || !formData.doorNumber) {
      alert('Please enter location and door number.');
      return;
    }
    try {
      const res = await fetch(`http://localhost:5000/api/workers/${selectedWorker._id}/book`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      if (res.ok) {
        alert('Booking confirmed!');
        setWorkers((prev) =>
          prev.map((w) => (w._id === selectedWorker._id ? { ...w, available: false } : w))
        );
        setBookedWorkerIds((prev) => [...prev, selectedWorker._id]);
        setShowBookingPopup(false);
        setTimeout(() => {
          setWorkers((prev) =>
            prev.map((w) => (w._id === selectedWorker._id ? { ...w, available: true } : w))
          );
          setBookedWorkerIds((prev) => prev.filter(id => id !== selectedWorker._id));
        }, 5 * 60 * 1000);
      }
    } catch (err) {
      alert('Booking failed. Please try again.');
    }
  };

  const handleContact = () => {
    if (selectedWorker?.phone) {
      window.location.href = `tel:${selectedWorker.phone}`;
    } else {
      alert('Phone number not available.');
    }
  };

  const handleTrack = async () => {
    if (!selectedWorker || !selectedWorker.location) {
      alert('Worker location not available.');
      return;
    }

    try {
      const url = `https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(selectedWorker.location)}&format=json&limit=1`;
      const response = await fetch(url);
      const data = await response.json();
      if (data.length > 0) {
        const coords = { lat: parseFloat(data[0].lat), lng: parseFloat(data[0].lon) };
        setWorkerCoords(coords); // NEW: set the worker coordinates
        setShowMap(true); // Show map when coordinates are available
      } else {
        alert('Location not found.');
      }
    } catch (err) {
      alert('Error fetching worker coordinates.');
    }
  };

  const handleClosePopup = () => {
    setShowBookingPopup(false);
    setShowMap(false);
  };

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <h2 style={{ textAlign: 'center', margin: '20px 0' }}>Available Workers</h2>

      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
        gap: '20px'
      }}>
        {workers.map((worker) => (
          <div key={worker._id} style={{
            border: '1px solid #ddd',
            borderRadius: '10px',
            padding: '15px',
            boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
          }}>
            <h3>{worker.name}</h3>
            <p>Specification: {worker.specification}</p>
            <p>Experience: {worker.experience} years</p>
            <p>Location: {worker.location}</p>
            <p>Phone: {worker.phone}</p>
            <p>Available: {worker.available ? 'Yes' : 'No'}</p>
            <button onClick={() => handleBookClick(worker)} disabled={!worker.available}>Book</button>
          </div>
        ))}
      </div>

      {showBookingPopup && (
        <div style={{
          position: 'fixed',
          top: '50%', left: '50%',
          transform: 'translate(-50%, -50%)',
          background: '#fff',
          padding: '20px',
          borderRadius: '10px',
          boxShadow: '0 2px 10px rgba(0,0,0,0.3)',
          zIndex: 1000
        }}>
          <h3>Booking: {selectedWorker?.name}</h3>
          <label>
            Location:
            <input
              type="text"
              name="location"
              value={formData.location}
              onChange={handleBookingFormChange}
            />
          </label>
          <br />
          <label>
            Door Number:
            <input
              type="text"
              name="doorNumber"
              value={formData.doorNumber}
              onChange={handleBookingFormChange}
            />
          </label>
          <br />
          {distance && arrivalTime && (
            <>
              <p>Distance: {distance}</p>
              <p>Arrival Time: {arrivalTime}</p>
            </>
          )}
          <button onClick={handleConfirmBooking}>Confirm Booking</button>
          <button onClick={handleContact}>Contact Worker</button>
          <button onClick={handleTrack}>Track Worker</button> {/* NEW: Track Button */}
          <button onClick={handleClosePopup}>Close</button>
        </div>
      )}

      {showMap && workerCoords && userCoords && (
        <TrackWorker userCoords={userCoords} workerCoords={workerCoords} />
      )}
    </div>
  );
};

export default WorkersList;
