import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Polyline, Popup } from 'react-leaflet';

const TrackWorker = ({ userLocation, workerId }) => {
  const [workerLocation, setWorkerLocation] = useState(null);

  useEffect(() => {
    const interval = setInterval(async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/workers/${workerId}/location`);
        const data = await res.json();
        setWorkerLocation(data);
      } catch (err) {
        console.error('Error fetching worker location:', err);
      }
    }, 5000);
    return () => clearInterval(interval);
  }, [workerId]);

  if (!userLocation || !workerLocation) return <p>Loading map...</p>;

  return (
    <MapContainer center={userLocation} zoom={13} style={{ height: '400px', width: '100%' }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      <Marker position={userLocation}><Popup>Your Home</Popup></Marker>
      <Marker position={workerLocation}><Popup>Worker</Popup></Marker>
      <Polyline positions={[userLocation, workerLocation]} color="blue" />
    </MapContainer>
  );
};

export default TrackWorker;
