import React, { useEffect, useState } from 'react';

const WorkersList = () => {
  const [workers, setWorkers] = useState([]);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [formData, setFormData] = useState({ location: '', doorNumber: '' });
  const [showInfo, setShowInfo] = useState(false);
  const [distance, setDistance] = useState('');
  const [arrivalTime, setArrivalTime] = useState('');
  const [bookedWorkerIds, setBookedWorkerIds] = useState([]);
  const [workerCoords, setWorkerCoords] = useState(null);
  const [showMap, setShowMap] = useState(false);

  useEffect(() => {
    const fetchWorkers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/workers');
        const data = await res.json();
        setWorkers(data);
      } catch (err) {
        console.error('Failed to fetch workers:', err);
      }
    };
    fetchWorkers();
  }, []);

  const handleBookNow = (worker) => {
    setSelectedWorker(worker);
    setFormData({ location: '', doorNumber: '' });
    setShowInfo(false);
    setDistance('');
    setArrivalTime('');
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    const updatedFormData = { ...formData, [name]: value };
    setFormData(updatedFormData);

    if (updatedFormData.location && updatedFormData.doorNumber) {
      const randomDistance = (Math.random() * (10 - 2) + 2).toFixed(2);
      const randomTime = Math.floor(Math.random() * (25 - 10) + 10);
      setDistance(`${randomDistance} km`);
      setArrivalTime(`${randomTime} minutes`);
      setShowInfo(true);
    }
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`http://localhost:5000/api/workers/${selectedWorker._id}/book`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      if (res.ok) {
        alert('Booking confirmed and email sent!');
        setWorkers((prev) =>
          prev.map((w) => (w._id === selectedWorker._id ? { ...w, available: false } : w))
        );
        setBookedWorkerIds((prev) => [...prev, selectedWorker._id]);
        setSelectedWorker(null); // Close modal
        setFormData({ location: '', doorNumber: '' });

        // Make worker available again after 5 minutes
        setTimeout(() => {
          setWorkers((prev) =>
            prev.map((w) => (w._id === selectedWorker._id ? { ...w, available: true } : w))
          );
        }, 5 * 60 * 1000);
      } else {
        const errorData = await res.json();
        console.error('Booking error:', errorData);
        alert('Failed to book the worker.');
      }
    } catch (err) {
      console.error('Booking failed:', err);
      alert('Booking failed due to network/server error.');
    }
  };

  const handleTrack = (worker) => {
    if (worker.latitude && worker.longitude) {
      setWorkerCoords({ lat: worker.latitude, lng: worker.longitude });
      setShowMap(true);
    } else {
      alert('Worker location data is missing.');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '2rem' }}>Workers List</h2>
      {workers.length === 0 ? (
        <p style={{ textAlign: 'center' }}>No workers found.</p>
      ) : (
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
            gap: '1.5rem',
          }}
        >
          {workers.map((worker) => (
            <div
              key={worker._id}
              style={{
                border: '1px solid #ddd',
                borderRadius: '12px',
                padding: '1rem',
                boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
                textAlign: 'center',
              }}
            >
              {worker.photo ? (
                <img
                  src={`http://localhost:5000/uploads/${worker.photo}`}
                  alt={worker.name}
                  style={{
                    width: '100px',
                    height: '100px',
                    objectFit: 'cover',
                    borderRadius: '50%',
                    marginBottom: '1rem',
                  }}
                />
              ) : (
                <div
                  style={{
                    width: '100px',
                    height: '100px',
                    borderRadius: '50%',
                    backgroundColor: '#eee',
                    margin: '0 auto 1rem',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '14px',
                    color: '#888',
                  }}
                >
                  No Photo
                </div>
              )}
              <h3>{worker.name}</h3>
              <p><strong>Work:</strong> {worker.workSpecification || 'N/A'}</p>
              <p><strong>Experience:</strong> {worker.experience || 'N/A'}</p>
              <p><strong>Location:</strong> {worker.location || 'N/A'}</p>
              <p><strong>Phone:</strong> {worker.phone || 'N/A'}</p>
              <p>
                <strong>Status:</strong>{' '}
                <span style={{ color: worker.available ? 'green' : 'red' }}>
                  {worker.available ? 'Available' : 'Unavailable'}
                </span>
              </p>
              <button
                disabled={!worker.available}
                onClick={() => handleBookNow(worker)}
                style={{
                  padding: '0.5rem 1rem',
                  marginTop: '0.5rem',
                  backgroundColor: worker.available ? '#007bff' : '#999',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: worker.available ? 'pointer' : 'not-allowed',
                }}
              >
                Book Now
              </button>
              {bookedWorkerIds.includes(worker._id) && (
                <button
                  onClick={() => handleTrack(worker)}
                  style={{
                    marginTop: '0.5rem',
                    backgroundColor: '#28a745',
                    color: '#fff',
                    padding: '0.5rem 1rem',
                    border: 'none',
                    borderRadius: '6px',
                    cursor: 'pointer',
                  }}
                >
                  Track
                </button>
              )}
            </div>
          ))}
        </div>
      )}

      {selectedWorker && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100vw',
            height: '100vh',
            backgroundColor: 'rgba(0,0,0,0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 9999,
          }}
        >
          <div
            style={{
              backgroundColor: '#fff',
              padding: '2rem',
              borderRadius: '12px',
              width: '300px',
              position: 'relative',
            }}
          >
            <h3>Book {selectedWorker.name}</h3>
            <form onSubmit={handleFormSubmit}>
              <div style={{ marginBottom: '1rem' }}>
                <label htmlFor="location">Your Location (District):</label>
                <input
                  type="text"
                  id="location"
                  name="location"
                  value={formData.location}
                  onChange={handleInputChange}
                  placeholder="e.g. Bangalore Urban"
                  required
                  style={{
                    width: '100%',
                    padding: '0.5rem',
                    borderRadius: '6px',
                    border: '1px solid #ccc',
                  }}
                />
              </div>
              <div style={{ marginBottom: '1rem' }}>
                <label htmlFor="doorNumber">Door Number:</label>
                <input
                  type="text"
                  id="doorNumber"
                  name="doorNumber"
                  value={formData.doorNumber}
                  onChange={handleInputChange}
                  placeholder="e.g. 123"
                  required
                  style={{
                    width: '100%',
                    padding: '0.5rem',
                    borderRadius: '6px',
                    border: '1px solid #ccc',
                  }}
                />
              </div>

              {showInfo && (
                <div
                  style={{
                    backgroundColor: '#f8f9fa',
                    padding: '0.75rem',
                    borderRadius: '6px',
                    marginBottom: '1rem',
                    fontSize: '14px',
                    color: '#333',
                  }}
                >
                  <p>Distance: {distance}</p>
                  <p>Approx. Arrival Time: {arrivalTime}</p>
                </div>
              )}

              <button
                type="submit"
                style={{
                  backgroundColor: '#007bff',
                  color: '#fff',
                  border: 'none',
                  padding: '0.75rem 1rem',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  width: '100%',
                }}
              >
                Confirm Booking
              </button>
            </form>

            <button
              onClick={() => setSelectedWorker(null)}
              style={{
                position: 'absolute',
                top: '10px',
                right: '15px',
                background: 'transparent',
                border: 'none',
                fontSize: '1.5rem',
                cursor: 'pointer',
                color: '#999',
              }}
              aria-label="Close"
            >
              &times;
            </button>
          </div>
        </div>
      )}

      {showMap && workerCoords && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100vw',
            height: '100vh',
            backgroundColor: 'rgba(0,0,0,0.6)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 9999,
          }}
          onClick={() => setShowMap(false)}
        >
          <div
            style={{
              width: '90vw',
              height: '70vh',
              borderRadius: '12px',
              overflow: 'hidden',
              boxShadow: '0 2px 10px rgba(0,0,0,0.3)',
              position: 'relative',
              backgroundColor: '#fff',
            }}
            onClick={(e) => e.stopPropagation()}
          >
            <iframe
              width="100%"
              height="100%"
              style={{ border: 0 }}
              src={`https://www.openstreetmap.org/export/embed.html?bbox=${workerCoords.lng - 0.01}%2C${workerCoords.lat - 0.01}%2C${workerCoords.lng + 0.01}%2C${workerCoords.lat + 0.01}&layer=mapnik&marker=${workerCoords.lat}%2C${workerCoords.lng}`}
              allowFullScreen
              loading="lazy"
              title="Worker Location Map"
            ></iframe>
            <button
              onClick={() => setShowMap(false)}
              style={{
                position: 'absolute',
                top: '10px',
                right: '10px',
                background: '#ff4d4d',
                color: '#fff',
                border: 'none',
                borderRadius: '6px',
                padding: '0.5rem 1rem',
                cursor: 'pointer',
              }}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default WorkersList;
