import React, { useState, useEffect } from 'react';

// Sample data of 20 workers
const initialWorkers = Array.from({ length: 20 }, (_, i) => ({
  id: i + 1,
  name: `Worker ${i + 1}`,
  location: `Location ${i + 1}`,
  experience: `${(i % 10) + 1} years`,
  available: true,
}));

export default function Services() {
  const [workers, setWorkers] = useState(initialWorkers);

  // Handler for booking a worker
  const bookWorker = (id) => {
    setWorkers((prevWorkers) =>
      prevWorkers.map((w) => {
        if (w.id === id && w.available) {
          return { ...w, available: false };
        }
        return w;
      })
    );

    // Set timer to reset availability after 5 minutes (300000 ms)
    setTimeout(() => {
      setWorkers((prevWorkers) =>
        prevWorkers.map((w) => {
          if (w.id === id) {
            return { ...w, available: true };
          }
          return w;
        })
      );
    }, 300000); // 5 minutes
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Services - Blue Collar Workers</h1>
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill,minmax(250px,1fr))',
          gap: '20px',
        }}
      >
        {workers.map((worker) => (
          <div
            key={worker.id}
            style={{
              border: '1px solid #ccc',
              borderRadius: '8px',
              padding: '15px',
              boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
            }}
          >
            <h3>{worker.name}</h3>
            <p><strong>Location:</strong> {worker.location}</p>
            <p><strong>Experience:</strong> {worker.experience}</p>
            <p>
              <strong>Status:</strong>{' '}
              {worker.available ? (
                <span style={{ color: 'green' }}>Available</span>
              ) : (
                <span style={{ color: 'red' }}>Unavailable</span>
              )}
            </p>
            <button
              onClick={() => bookWorker(worker.id)}
              disabled={!worker.available}
              style={{
                backgroundColor: worker.available ? '#007bff' : '#ccc',
                color: 'white',
                border: 'none',
                padding: '10px 15px',
                borderRadius: '5px',
                cursor: worker.available ? 'pointer' : 'not-allowed',
              }}
            >
              {worker.available ? 'Book Now' : 'Booked'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
