const servicesData = [
  { id: 1, name: 'Plumber', description: 'Expert in fixing leaks, pipes, and water systems.' },
  { id: 2, name: 'Electrician', description: 'Certified to handle electrical wiring and repairs.' },
  { id: 3, name: 'Contractor', description: 'Oversees construction projects from start to finish.' },
  // Add more services up to 20 or however many you want
];

const Services = () => {
  return (
    <section style={serviceStyles.container}>
      <h2>Our Services</h2>
      <div style={serviceStyles.list}>
        {servicesData.map(service => (
          <div key={service.id} style={serviceStyles.card}>
            <h3>{service.name}</h3>
            <p>{service.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

const serviceStyles = {
  container: {
    padding: '40px 20px',
    backgroundColor: 'rgba(0,0,0,0.6)',
    color: 'white',
    marginTop: '40vh', // push it down below welcome message
  },
  list: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
    gap: '20px',
  },
  card: {
    flex: '1 1 200px',
    backgroundColor: 'rgba(255,255,255,0.1)',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 0 10px rgba(0,0,0,0.5)',
    textAlign: 'center',
  },
};
