import React, { useEffect, useState } from 'react';
import TrackWorker from './TrackWorker'; // Your tracking component
import 'leaflet/dist/leaflet.css';

const WorkersList = () => {
  const [workers, setWorkers] = useState([]);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [formData, setFormData] = useState({ location: '', doorNumber: '' });
  const [showInfo, setShowInfo] = useState(false);
  const [distance, setDistance] = useState('');
  const [arrivalTime, setArrivalTime] = useState('');
  const [trackingWorkerId, setTrackingWorkerId] = useState(null);
  const [userLocationCoords, setUserLocationCoords] = useState(null);

  useEffect(() => {
    // Fetch all workers on mount
    const fetchWorkers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/workers');
        const data = await res.json();
        setWorkers(data);
      } catch (err) {
        console.error('Failed to fetch workers:', err);
      }
    };
    fetchWorkers();
  }, []);

  const handleBookNow = (worker) => {
    setSelectedWorker(worker);
    setFormData({ location: '', doorNumber: '' });
    setShowInfo(false);
    setDistance('');
    setArrivalTime('');
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    const updatedFormData = { ...formData, [name]: value };
    setFormData(updatedFormData);

    // Show estimated distance & arrival time if both fields filled
    if (updatedFormData.location && updatedFormData.doorNumber) {
      const randomDistance = (Math.random() * (10 - 2) + 2).toFixed(2);
      const randomTime = Math.floor(Math.random() * (25 - 10) + 10);
      setDistance(`${randomDistance} km`);
      setArrivalTime(`${randomTime} minutes`);
      setShowInfo(true);
    } else {
      setShowInfo(false);
      setDistance('');
      setArrivalTime('');
    }
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    // Try to get user GPS coordinates from browser
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          sendBookingRequest(pos.coords.latitude, pos.coords.longitude);
        },
        (err) => {
          console.warn('Geolocation denied or error. Booking without coords.');
          sendBookingRequest(null, null);
        }
      );
    } else {
      sendBookingRequest(null, null);
    }
  };

  // Actual booking API call
  const sendBookingRequest = async (userLatitude, userLongitude) => {
    try {
      const res = await fetch(`http://localhost:5000/api/workers/${selectedWorker._id}/book`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...formData,
          userLatitude,
          userLongitude,
        }),
      });

      if (res.ok) {
        alert('Booking confirmed! Email sent to the worker.');

        // Save user coords or fallback coords (change to your default)
        setUserLocationCoords(
          userLatitude && userLongitude ? [userLatitude, userLongitude] : [12.9716, 77.5946]
        );

        // Update workers state: mark booked and unavailable
        setWorkers((prev) =>
          prev.map((w) =>
            w._id === selectedWorker._id ? { ...w, available: false, booked: true } : w
          )
        );

        setTrackingWorkerId(selectedWorker._id);

        // Reset after 5 minutes
        setTimeout(() => {
          setWorkers((prev) =>
            prev.map((w) =>
              w._id === selectedWorker._id ? { ...w, available: true, booked: false } : w
            )
          );
          setTrackingWorkerId(null);
        }, 5 * 60 * 1000);

        setSelectedWorker(null);
        setFormData({ location: '', doorNumber: '' });
      } else {
        const errorData = await res.json();
        alert('Failed to book: ' + (errorData.message || 'Unknown error'));
      }
    } catch (err) {
      alert('Booking failed: Network/server error');
      console.error(err);
    }
  };

  const handleCancelBooking = async (worker) => {
    if (!window.confirm(`Cancel booking for ${worker.name}?`)) return;

    try {
      const res = await fetch(`http://localhost:5000/api/workers/${worker._id}/cancel`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });

      if (res.ok) {
        alert('Booking canceled. Email sent to the worker.');

        setWorkers((prev) =>
          prev.map((w) =>
            w._id === worker._id ? { ...w, available: true, booked: false } : w
          )
        );

        if (trackingWorkerId === worker._id) {
          setTrackingWorkerId(null);
          setUserLocationCoords(null);
        }
      } else {
        const errorData = await res.json();
        alert('Failed to cancel: ' + (errorData.message || 'Unknown error'));
      }
    } catch (err) {
      alert('Cancel booking failed: Network/server error');
      console.error(err);
    }
  };

  const handleTrackClick = (workerId) => {
    setTrackingWorkerId(workerId);
    setUserLocationCoords([12.9716, 77.5946]); // Default user location fallback
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '2rem' }}>Workers List</h2>

      {workers.length === 0 && <p style={{ textAlign: 'center' }}>No workers found.</p>}

      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
          gap: '1.5rem',
        }}
      >
        {workers.map((worker) => (
          <div
            key={worker._id}
            style={{
              border: '1px solid #ddd',
              borderRadius: '12px',
              padding: '1rem',
              boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
              textAlign: 'center',
            }}
          >
            {worker.photo ? (
              <img
                src={`http://localhost:5000/uploads/${worker.photo}`}
                alt={worker.name}
                style={{
                  width: '100px',
                  height: '100px',
                  objectFit: 'cover',
                  borderRadius: '50%',
                  marginBottom: '1rem',
                }}
              />
            ) : (
              <div
                style={{
                  width: '100px',
                  height: '100px',
                  borderRadius: '50%',
                  backgroundColor: '#eee',
                  margin: '0 auto 1rem',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '14px',
                  color: '#888',
                }}
              >
                No Photo
              </div>
            )}

            <h3>{worker.name}</h3>
            <p>
              <strong>Work:</strong> {worker.workSpecification || 'N/A'}
            </p>
            <p>
              <strong>Experience:</strong> {worker.experience || 'N/A'}
            </p>
            <p>
              <strong>Location:</strong> {worker.location || 'N/A'}
            </p>
            <p>
              <strong>Status:</strong>{' '}
              <span style={{ color: worker.available ? 'green' : 'red' }}>
                {worker.available ? 'Available' : 'Unavailable'}
              </span>
            </p>

            {!worker.booked ? (
              <button
                disabled={!worker.available}
                onClick={() => handleBookNow(worker)}
                style={{
                  padding: '0.5rem 1rem',
                  marginTop: '0.5rem',
                  backgroundColor: worker.available ? '#007bff' : '#ccc',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '8px',
                  cursor: worker.available ? 'pointer' : 'not-allowed',
                }}
              >
                Book Now
              </button>
            ) : (
              <>
                <button
                  onClick={() => handleTrackClick(worker._id)}
                  style={{
                    padding: '0.4rem 1rem',
                    marginTop: '0.5rem',
                    backgroundColor: '#28a745',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '8px',
                    cursor: 'pointer',
                    marginRight: '0.5rem',
                  }}
                >
                  Track
                </button>
                <button
                  onClick={() => handleCancelBooking(worker)}
                  style={{
                    padding: '0.4rem 1rem',
                    marginTop: '0.5rem',
                    backgroundColor: '#dc3545',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '8px',
                    cursor: 'pointer',
                  }}
                >
                  Cancel Booking
                </button>
              </>
            )}
          </div>
        ))}
      </div>

      {selectedWorker && (
        <div
          style={{
            position: 'fixed',
            top: '20%',
            left: '30%',
            width: '40%',
            backgroundColor: '#fff',
            border: '2px solid #007bff',
            borderRadius: '10px',
            padding: '2rem',
            zIndex: 1000,
          }}
        >
          <div style={{ textAlign: 'right' }}>
            <button
              onClick={() => setSelectedWorker(null)}
              style={{ background: 'none', border: 'none', fontSize: '20px', cursor: 'pointer' }}
            >
              ×
            </button>
          </div>
          <h3>Book {selectedWorker.name}</h3>
          <form onSubmit={handleFormSubmit}>
            <input
              name="location"
              type="text"
              placeholder="Your Location"
              value={formData.location}
              onChange={handleInputChange}
              required
              style={{ display: 'block', width: '100%', marginBottom: '1rem', padding: '0.5rem' }}
            />
            <input
              name="doorNumber"
              type="text"
              placeholder="Door Number"
              value={formData.doorNumber}
              onChange={handleInputChange}
              required
              style={{ display: 'block', width: '100%', marginBottom: '1rem', padding: '0.5rem' }}
            />
            {showInfo && (
              <>
                <p>
                  <strong>Estimated Distance:</strong> {distance}
                </p>
                <p>
                  <strong>Estimated Arrival Time:</strong> {arrivalTime}
                </p>
              </>
            )}
            <button
              type="submit"
              style={{
                padding: '0.5rem 1rem',
                backgroundColor: '#007bff',
                color: '#fff',
                border: 'none',
                borderRadius: '8px',
                cursor: 'pointer',
              }}
            >
              Confirm Booking
            </button>
          </form>
        </div>
      )}

      {trackingWorkerId && userLocationCoords && (
        <TrackWorker workerId={trackingWorkerId} userLocationCoords={userLocationCoords} />
      )}
    </div>
  );
};

export default WorkersList;
