import React, { useEffect, useState } from 'react';

const WorkersList = () => {
  const [workers, setWorkers] = useState([]);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [formData, setFormData] = useState({ location: '', doorNumber: '' });

  useEffect(() => {
    const fetchWorkers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/workers');
        const data = await res.json();
        setWorkers(data);
      } catch (err) {
        console.error('Failed to fetch workers:', err);
      }
    };

    fetchWorkers();
  }, []);

  const handleBookNow = (worker) => {
    setSelectedWorker(worker);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch(`http://localhost:5000/api/workers/${selectedWorker._id}/book`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      if (res.ok) {
        alert('Booking confirmed and email sent!');
        setWorkers((prev) =>
          prev.map((w) => w._id === selectedWorker._id ? { ...w, available: false } : w)
        );
        setSelectedWorker(null);
        setFormData({ location: '', doorNumber: '' });

        // Auto-available after 5 mins (simulate or backend can manage too)
        setTimeout(() => {
          setWorkers((prev) =>
            prev.map((w) => w._id === selectedWorker._id ? { ...w, available: true } : w)
          );
        }, 5 * 60 * 1000);
      }
    } catch (err) {
      console.error('Booking failed:', err);
      alert('Failed to book the worker.');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '2rem' }}>Workers List</h2>
      {workers.length === 0 ? (
        <p style={{ textAlign: 'center' }}>No workers found.</p>
      ) : (
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
            gap: '1.5rem',
          }}
        >
          {workers.map((worker) => (
            <div
              key={worker._id}
              style={{
                border: '1px solid #ddd',
                borderRadius: '12px',
                padding: '1rem',
                boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
                textAlign: 'center',
              }}
            >
              {worker.photo ? (
                <img
                  src={`http://localhost:5000/uploads/${worker.photo}`}
                  alt={worker.name}
                  style={{
                    width: '100px',
                    height: '100px',
                    objectFit: 'cover',
                    borderRadius: '50%',
                    marginBottom: '1rem',
                  }}
                />
              ) : (
                <div
                  style={{
                    width: '100px',
                    height: '100px',
                    borderRadius: '50%',
                    backgroundColor: '#eee',
                    margin: '0 auto 1rem',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '14px',
                    color: '#888',
                  }}
                >
                  No Photo
                </div>
              )}
              <h3>{worker.name}</h3>
              <p><strong>Work:</strong> {worker.workSpecification || 'N/A'}</p>
              <p><strong>Experience:</strong> {worker.experience || 'N/A'}</p>
              <p><strong>Location:</strong> {worker.location || 'N/A'}</p>
              <p>
                <strong>Status:</strong>{' '}
                <span style={{ color: worker.available ? 'green' : 'red' }}>
                  {worker.available ? 'Available' : 'Unavailable'}
                </span>
              </p>
              <button
                disabled={!worker.available}
                onClick={() => handleBookNow(worker)}
                style={{
                  padding: '0.5rem 1rem',
                  marginTop: '0.5rem',
                  backgroundColor: worker.available ? '#007bff' : '#ccc',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '8px',
                  cursor: worker.available ? 'pointer' : 'not-allowed',
                }}
              >
                Book Now
              </button>
            </div>
          ))}
        </div>
      )}

      {selectedWorker && (
        <div style={{
          position: 'fixed',
          top: '20%',
          left: '30%',
          width: '40%',
          backgroundColor: '#fff',
          border: '2px solid #007bff',
          borderRadius: '10px',
          padding: '2rem',
          zIndex: 1000,
        }}>
          <h3>Book {selectedWorker.name}</h3>
          <form onSubmit={handleFormSubmit}>
            <input
              type="text"
              placeholder="Your Location"
              value={formData.location}
              onChange={(e) => setFormData({ ...formData, location: e.target.value })}
              required
              style={{ display: 'block', width: '100%', marginBottom: '1rem', padding: '0.5rem' }}
            />
            <input
              type="text"
              placeholder="Door Number"
              value={formData.doorNumber}
              onChange={(e) => setFormData({ ...formData, doorNumber: e.target.value })}
              required
              style={{ display: 'block', width: '100%', marginBottom: '1rem', padding: '0.5rem' }}
            />
            <p><strong>Estimated Distance:</strong> 4.8 km</p>
            <p><strong>Minimum Arrival Time:</strong> 12 minutes</p>
            <button type="submit" style={{ backgroundColor: '#28a745', padding: '0.5rem 1rem', color: '#fff', border: 'none', borderRadius: '6px' }}>
              Confirm Booking
            </button>
            <button type="button" onClick={() => setSelectedWorker(null)} style={{ marginLeft: '1rem' }}>
              Cancel
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default WorkersList;
