import React, { useEffect, useState } from 'react';

const WorkersList = () => {
  const [workers, setWorkers] = useState([]);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [formData, setFormData] = useState({ location: '', doorNumber: '' });
  const [showInfo, setShowInfo] = useState(false);
  const [distance, setDistance] = useState('');
  const [arrivalTime, setArrivalTime] = useState('');
  const [bookedWorkerIds, setBookedWorkerIds] = useState([]);

  useEffect(() => {
    const fetchWorkers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/workers');
        const data = await res.json();
        setWorkers(data);
      } catch (err) {
        console.error('Failed to fetch workers:', err);
      }
    };

    const fetchUserLocation = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/users/me');
        const data = await res.json();
        if (data.latitude && data.longitude) {
          // Do something with user coordinates if needed
        }
      } catch (err) {
        console.error('Failed to fetch user location:', err);
      }
    };

    fetchWorkers();
    fetchUserLocation();
  }, []);

  const handleBookNow = (worker) => {
    setSelectedWorker(worker);
    setFormData({ location: '', doorNumber: '' });
    setShowInfo(false);
    setDistance('');
    setArrivalTime('');
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    const updatedFormData = { ...formData, [name]: value };
    setFormData(updatedFormData);
    if (updatedFormData.location && updatedFormData.doorNumber) {
      const randomDistance = (Math.random() * (10 - 2) + 2).toFixed(2);
      const randomTime = Math.floor(Math.random() * (25 - 10) + 10);
      setDistance(`${randomDistance} km`);
      setArrivalTime(`${randomTime} minutes`);
      setShowInfo(true);
    }
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`http://localhost:5000/api/workers/${selectedWorker._id}/book`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      if (res.ok) {
        setWorkers((prev) =>
          prev.map((w) => (w._id === selectedWorker._id ? { ...w, available: false } : w))
        );
        setBookedWorkerIds((prev) => [...prev, selectedWorker._id]);
        setSelectedWorker(null);
        setFormData({ location: '', doorNumber: '' });
        setShowInfo(false);
        setDistance('');
        setArrivalTime('');
        setTimeout(() => {
          setWorkers((prev) =>
            prev.map((w) => (w._id === selectedWorker._id ? { ...w, available: true } : w))
          );
        }, 5 * 60 * 1000);
      } else {
        const errorData = await res.json();
        console.error('Booking error:', errorData);
        alert('Failed to book the worker.');
      }
    } catch (err) {
      console.error('Booking failed:', err);
      alert('An unexpected error occurred while booking.');
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Available Workers</h2>
      <ul style={{ listStyleType: 'none', padding: 0 }}>
        {workers.map((worker) => (
          <li
            key={worker._id}
            style={{
              marginBottom: '10px',
              padding: '10px',
              border: '1px solid #ccc',
              borderRadius: '5px',
              backgroundColor: '#f9f9f9',
            }}
          >
            <strong>{worker.name}</strong> - {worker.location} -{' '}
            {worker.available ? (
              <button
                onClick={() => handleBookNow(worker)}
                style={{
                  padding: '5px 10px',
                  backgroundColor: '#4CAF50',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer',
                }}
              >
                Book Now
              </button>
            ) : (
              <span style={{ color: 'red' }}>Unavailable</span>
            )}
          </li>
        ))}
      </ul>

      {selectedWorker && (
        <div
          style={{
            border: '1px solid gray',
            padding: '15px',
            marginTop: '20px',
            maxWidth: '400px',
            backgroundColor: '#fff',
            borderRadius: '8px',
          }}
        >
          <h3 style={{ marginBottom: '10px' }}>
            Booking Form for {selectedWorker.name}
          </h3>
          <form onSubmit={handleFormSubmit}>
            <div style={{ marginBottom: '10px' }}>
              <label style={{ display: 'block', marginBottom: '5px' }}>Location:</label>
              <input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleInputChange}
                required
                style={{
                  width: '100%',
                  padding: '8px',
                  borderRadius: '4px',
                  border: '1px solid #ccc',
                }}
              />
            </div>
            <div style={{ marginBottom: '10px' }}>
              <label style={{ display: 'block', marginBottom: '5px' }}>Door Number:</label>
              <input
                type="text"
                name="doorNumber"
                value={formData.doorNumber}
                onChange={handleInputChange}
                required
                style={{
                  width: '100%',
                  padding: '8px',
                  borderRadius: '4px',
                  border: '1px solid #ccc',
                }}
              />
            </div>
            {showInfo && (
              <div style={{ marginBottom: '10px' }}>
                <p>
                  <strong>Distance:</strong> {distance}
                </p>
                <p>
                  <strong>Arrival Time:</strong> {arrivalTime}
                </p>
              </div>
            )}
            <button
              type="submit"
              style={{
                padding: '8px 15px',
                backgroundColor: '#007BFF',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
              }}
            >
              Book Worker
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default WorkersList;
