import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix leaflet icon issues
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const TrackWorker = ({ bookingId }) => {
  const [workerLocation, setWorkerLocation] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [mapError, setMapError] = useState(false);

  useEffect(() => {
    const fetchBookingData = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/bookings/${bookingId}`);
        const data = await res.json();

        if (!data.worker || !data.user) {
          setMapError(true);
          return;
        }

        const { latitude: wLat, longitude: wLng } = data.worker;
        const { latitude: uLat, longitude: uLng } = data.user;

        if (wLat && wLng && uLat && uLng) {
          setWorkerLocation([wLat, wLng]);
          setUserLocation([uLat, uLng]);
        } else {
          setMapError(true);
        }
      } catch (error) {
        console.error('Failed to fetch booking data:', error);
        setMapError(true);
      }
    };

    fetchBookingData();
  }, [bookingId]);

  if (mapError) {
    return <p style={{ textAlign: 'center', color: 'red' }}>Map failed to load. Check coordinates or backend.</p>;
  }

  if (!userLocation || !workerLocation) {
    return <p style={{ textAlign: 'center' }}>Loading map...</p>;
  }

  return (
    <MapContainer center={userLocation} zoom={13} style={{ height: '400px', width: '100%' }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      <Marker position={userLocation}>
        <Popup>You (User)</Popup>
      </Marker>
      <Marker position={workerLocation}>
        <Popup>Worker</Popup>
      </Marker>
    </MapContainer>
  );
};

export default TrackWorker;
