import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Message = ({ receiverId, onClose }) => {
  const [content, setContent] = useState('');
  const [senderId, setSenderId] = useState('');
  const [status, setStatus] = useState('');
  const [loading, setLoading] = useState(true); // for senderId fetching

  useEffect(() => {
    const fetchSenderId = async () => {
      try {
        const user = JSON.parse(localStorage.getItem('user'));

        if (user?.email) {
          const res = await axios.get(`http://localhost:5000/api/users/email/${user.email}`);
          setSenderId(res.data._id);
        } else {
          setStatus('‚ö†Ô∏è User email not found in localStorage');
        }
      } catch (err) {
        console.error('‚ùå Error fetching sender ID:', err.response?.data || err.message);
        setStatus('‚ùå Could not fetch sender ID');
      } finally {
        setLoading(false); // fetch complete
      }
    };

    fetchSenderId();
  }, []);

  const sendMessage = async () => {
    if (!senderId) {
      setStatus('‚ö†Ô∏è Sender not ready yet.');
      return;
    }

    if (!content.trim()) {
      setStatus('‚ö†Ô∏è Please type a message.');
      return;
    }

    try {
      await axios.post('http://localhost:5000/api/messages', {
        senderId,
        receiverId,
        content,
      });

      setStatus('‚úÖ Message sent!');
      setContent('');
    } catch (error) {
      console.error('‚ùå Failed to send message:', error.response?.data || error.message);
      setStatus('‚ùå Failed to send message.');
    }
  };

  return (
    <div className="message-popup" style={popupStyle}>
      <h3>Send Message</h3>
      {loading ? (
        <p>üîÑ Loading sender info...</p>
      ) : (
        <>
          <textarea
            placeholder="Type your message..."
            value={content}
            onChange={(e) => setContent(e.target.value)}
            rows={4}
            style={textareaStyle}
          />
          <div>
            <button onClick={sendMessage} disabled={!senderId || !content.trim()} style={buttonStyle}>
              Send
            </button>
            <button onClick={onClose} style={{ ...buttonStyle, backgroundColor: '#ccc', color: '#000' }}>
              Close
            </button>
          </div>
          <p>{status}</p>
        </>
      )}
    </div>
  );
};

// Optional styles
const popupStyle = {
  padding: '1rem',
  border: '1px solid #ccc',
  borderRadius: '8px',
  backgroundColor: '#fff',
  maxWidth: '400px',
  margin: 'auto',
};

const textareaStyle = {
  width: '100%',
  padding: '0.5rem',
  marginBottom: '1rem',
  fontSize: '1rem',
};

const buttonStyle = {
  padding: '0.5rem 1rem',
  marginRight: '1rem',
  backgroundColor: '#2c3e50',
  color: 'white',
  border: 'none',
  cursor: 'pointer',
};

export default Message;
