import React, { useContext, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';   // <-- import useNavigate
import { AuthContext } from '../context/AuthContext';

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const [showDropdown, setShowDropdown] = useState(false);
  const navigate = useNavigate();   // <-- get navigate function

  const handleLogout = async () => {
    await logout();               // <-- wait for logout to complete
    navigate('/');               // <-- react-router redirect after logout
  };

  return (
    <>
      <style>{`
        .nav-link {
          color: white;
          text-decoration: none;
          font-weight: 500;
          margin: 0 1rem;
        }
        .nav-link:visited,
        .nav-link:hover,
        .nav-link:active,
        .nav-link:focus {
          color: white;
          text-decoration: none;
        }
      `}</style>

      <nav style={styles.navbar}>
        <div style={styles.logo}>ðŸŒ¿FixItNow</div>
        <div style={{ position: 'relative' }}>
          <Link className="nav-link" to="/">Home</Link>
          <Link className="nav-link" to="/workerslist">Services</Link>

          {!user ? (
            <>
              <Link className="nav-link" to="/signup">Signup</Link>
              <Link className="nav-link" to="/login">Login</Link>
            </>
          ) : (
            <div
              style={styles.accountIcon}
              onClick={() => setShowDropdown(!showDropdown)}
            >
              ðŸ‘¤
              {showDropdown && (
                <div style={styles.dropdown}>
                  <div style={styles.dropdownItem}>{user.username}</div>
                  <div style={styles.dropdownItem} onClick={handleLogout}>Logout</div>
                </div>
              )}
            </div>
          )}
        </div>
      </nav>
    </>
  );
};

const styles = {
  navbar: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: '1rem 2rem',
    backgroundColor: '#111827',
    color: 'white',
    alignItems: 'center',
  },
  logo: {
    fontWeight: 'bold',
    fontSize: '1.5rem',
  },
  accountIcon: {
    display: 'inline-block',
    marginLeft: '1rem',
    cursor: 'pointer',
    position: 'relative',
  },
  dropdown: {
    position: 'absolute',
    top: '2.5rem',
    right: 0,
    backgroundColor: '#fff',
    color: '#000',
    border: '1px solid #ccc',
    borderRadius: '5px',
    overflow: 'hidden',
  },
  dropdownItem: {
    padding: '0.5rem 1rem',
    cursor: 'pointer',
    borderBottom: '1px solid #eee',
  },
};

export default Navbar
