import React, { useEffect, useState } from 'react';

const WorkersList = () => {
  // This is required to define `setWorkers`
  const [workers, setWorkers] = useState([]);

  // Hooks must be used inside the component
  useEffect(() => {
    const fetchWorkers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/workers');
        const data = await res.json();
        setWorkers(data);
      } catch (err) {
        console.error('Failed to fetch workers:', err);
      }
    };

    fetchWorkers();
  }, []);

  return (
    <div>
      <h2>Workers List</h2>
      {workers.length === 0 ? (
        <p>No workers found.</p>
      ) : (
        <div>
          {workers.map((worker) => (
            <div key={worker._id} style={{ border: '1px solid #ccc', padding: '1rem', margin: '1rem 0' }}>
              <img
                src={`http://localhost:5000/uploads/${worker.photo}`}
                alt={worker.name}
                style={{ width: '100px', height: '100px', objectFit: 'cover' }}
              />
              <h3>{worker.name}</h3>
              <p>Work: {worker.workSpec}</p>
              <p>Experience: {worker.experience}</p>
              <p>Location: {worker.location}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default WorkersList;
