import React, { useEffect, useState } from 'react';

const Inbox = ({ workerId }) => {
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const fetchMessages = async () => {
      const res = await fetch(`http://localhost:5000/api/messages/${workerId}`);
      const data = await res.json();
      setMessages(data);
    };
    fetchMessages();
  }, [workerId]);

  return (
    <div style={{ padding: 20 }}>
      <h2>Your Inbox</h2>
      {messages.length === 0 ? (
        <p>No messages yet.</p>
      ) : (
        <ul>
          {messages.map((msg) => (
            <li key={msg._id} style={{ borderBottom: '1px solid #ccc', marginBottom: 10 }}>
              <p><strong>From:</strong> {msg.senderId.username}</p>
              <p>{msg.content}</p>
              <p style={{ fontSize: '12px', color: '#888' }}>{new Date(msg.timestamp).toLocaleString()}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Inbox;
