import React from 'react';
import {
  MapContainer,
  TileLayer,
  Marker,
  Polyline,
  Popup,
} from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

const TrackWorker = ({ userLat, userLng, workerLat, workerLng }) => {
  const userLocation = [userLat, userLng];
  const workerLocation = [workerLat, workerLng];
  const center = userLocation;

  return (
    <div style={{ height: '400px', width: '100%', marginTop: '20px' }}>
      <MapContainer center={center} zoom={13} style={{ height: '100%', width: '100%' }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        <Marker position={userLocation}>
          <Popup>User Location</Popup>
        </Marker>

        <Marker position={workerLocation}>
          <Popup>Worker Location</Popup>
        </Marker>

        <Polyline positions={[userLocation, workerLocation]} color="blue" />
      </MapContainer>
    </div>
  );
};

export default TrackWorker;
